       d.concepto,
       d.ori_idbanco,
       bo.nombre bancoOrigen,
       d.ori_cuenta,
       des_idbanco,
       bd.nombre bancoDestino,
       d.des_tipocuenta,
       simit.desencriptar(d.des_cuenta) cuentaDestino,
       d.des_nit,
       d.indtipo,
       d.valor,
       d.idmunicipio,

       (case
         when d.estadotrf is null then
          'ENV'
         else
          d.estadotrf
       end) as estadoTrf,

       d.fechaenvio,
       d.fechaconfirmada,
       d.fechadevolucion,
       d.motivodevolucion,
       d.codigodevolucion,

       (case
         when estadotrf is null then
          'Enviado sin confirmar'
         when estadotrf = 'ENV' then
          'Enviado sin confirmar'
         when estadotrf = 'EXI' then
          'Confirmada exitosamente'
         when estadotrf = 'REC' then
          motivodevolucion
         else
          'estado no definido'
       end) mensajeEstado,

       (case
         when estadotrf is null then
          d.fec_Dispersion
         when estadotrf = 'ENV' then
          d.fechaenvio
         when estadotrf = 'EXI' then
          d.Fechaconfirmada
