prompt ===================================
prompt
CREATE OR REPLACE PROCEDURE "SP_PARALELO_TEMP"(p_fecha date) is
  v_liquidacion       varchar2(10);
  v_fecha             date;
  v_comparendo        varchar2(20);
  v_validador         CHAR(3);
  v_indretroactividad number;
  v_contador          number := 0;

  v_numReg              number;
  v_consecutivo_cartera number;
  v_orden_comparendo    varchar(20);
  v_id_contraventor     varchar(15);
  v_encontro            number;

  v_resol           varchar(20);
  v_fecResol        date;
  v_validador_fecha char(3);
  v_indFecha2002    number;

begin
  for c in (select r.* from recaudo_externo r where r.fec_corte = p_fecha) loop
  
    v_liquidacion := c.num_liquidacion;
    v_comparendo  := null;
    v_fecha       := null;
    v_encontro    := 0;
  
    BEGIN
    
      select sdlc.ORDEN_COMPARENDO, sdlc.id_contraventor
        into v_orden_comparendo, v_id_contraventor
        from QUIPUX.SIMIT_DET_LIQUIDA_COMPARENDO SDLC
       where SDLC.COD_LIQUIDACION = SUBSTR(v_liquidacion, 1, 9);
    
      BEGIN
      
        SELECT SC.ORDEN_COMPARENDO, SC.FECHA
          into v_comparendo, v_fecha
          from QUIPUX.SIMIT_COMPARENDO SC
         where SC.ORDEN_COMPARENDO = v_orden_comparendo
           AND SC.DOCUMENTO_IDENTIDAD = v_id_contraventor
           and rownum = 1;
      
      EXCEPTION
        WHEN OTHERS THEN
          v_encontro := 0;
      END;
    
    EXCEPTION
      WHEN OTHERS THEN
        v_encontro := 0;
    END;
  
    IF (v_encontro = 0) then
      begin
      
        select SR.NRO_COMPARENDO,
               SR.FECHA_COMPARENDO,
               sr.nro_resolucion,
               sr.fecha_resolucion
          into v_comparendo, v_fecha, v_resol, v_fecResol
          from QUIPUX.SIMIT_RESOLUCION SR
         where SR.CONSECUTIVO_CARTERA =
               (select sdl.consecutivo_cartera
                  from QUIPUX.SIMIT_DETALLE_LIQUIDA SDL
                 where SDL.COD_LIQUIDACION = SUBSTR(v_liquidacion, 1, 9));
      
        if (v_comparendo = 'NO REPORTADO') then
          v_comparendo := v_resol;
          v_fecha      := v_fecResol;
        end if;
        v_encontro := 1;
      
      EXCEPTION
        WHEN OTHERS THEN
          v_encontro := 0;
      END;
    end if;
  
    v_validador_fecha := '000';
  
    begin
    
      select v.idvalidador
        into v_validador_fecha
        from validador_fecha v
       where v.idmunicipio = c.idmunicipio
         and v_fecha >= v.fecha_ini
         and v_fecha <= v.fecha_fin
         and substr(v_liquidacion, 8, 1) =
             decode(V.indtipo, 'SIMIT', 0, 'POLCA', 1);
    
    EXCEPTION
      WHEN OTHERS THEN
        v_validador_fecha := '000';
    END;
  
    v_indFecha2002 := 0;
  
    if v_fecha is not null then
      if to_char(v_fecha, 'ddmmyyyy') <> '01011900' then
        if v_fecha < to_date('081102', 'ddmmyy') then
          v_indFecha2002 := 1;
        end if;
      end if;
    end if;
  
    update recaudo_externo r
       set r.num_comparendo  = v_comparendo,
           r.fec_comparendo  = v_fecha,
           r.indfecha2002    = v_indFecha2002,
           r.validador_fecha = v_validador_fecha
     where r.num_liquidacion = c.num_liquidacion;
  
  end loop;

  commit;

end sp_paralelo_temp;
/

