    insert into acuerdopago
      (fec_pago,
       divipo,
       nombremunicipio,
       concepto,
       valor,
       idconcesionario,
       concesionario,
       vlrsimitantesprorroga,
       vlrpolcaantesprorroga,
       vlrsimitdespuesprorroga,
       vlrpolcadespuesprorroga,
       vlrreintegrarmun,
       idacuerdo,
       fechaliq,
       vigencia,
       fec_vig_ini,
       fec_vig_fin,
       estado,
       fechaestado,
       usuarioestado,
       observaciones,
       periodo,
       cuenta,
       idbanco, 
       porcentaje)
      select fec_pago,
             divipo,
             nombremunicipio,
             concepto,
             valor,
             idconcesionario,
             concesionario,
             vlrsimitantesprorroga,
             vlrpolcaantesprorroga,
             vlrsimitdespuesprorroga,
             vlrpolcadespuesprorroga,
             vlrreintegrarmun,
             seq_acuerdo.nextval,
             fechaliq,
             vigencia,
             fec_vig_ini,
             fec_vig_fin,
             estado,
             fechaestado,
             usuarioestado,
             observaciones,
             periodo,
             cuenta,
             idbanco,
             ( case when vlrsimitantesprorroga <> 0 then 10
                    when vlrsimitdespuesprorroga <> 0 then 10
                    else 55 end )
        from vw_acuerdopago
       where to_char(fec_pago, 'yyyymm') = to_char(v_fecha, 'yyyymm');
  
    commit;
  
    sp_log(V_USUARIO, 'insertarAcuerdos', to_char(v_fecha, 'yyyymm'));
    commit;
  
