    V_MSG_ERROR := 'Operacion Correcta';
  
  EXCEPTION
    WHEN OTHERS THEN
      v_cod_error := SQLCODE;
      v_msg_error := SQLERRM;
    
  END insertarAcuerdos;
PROCEDURE act_acuerdo(
                             p_usuario   varchar2,
                             p_idAcuerdo number,
                             p_vlrSimitAntes number,
                             p_vlrSimitDespues number,
                             p_vlrPolcaAntes number,
                             p_vlrPolcaDespues number,
                             p_vlrReintegro number,
                             p_porcentajePolca number,
                             p_vigIni date,
                             p_vigFin date,
                             p_estado varchar2,
                             p_observaciones varchar2,
                             p_codError out number,
                             p_msgError out varchar2) is

    numReg number;
    vlrTotal number;
    fecLiq date;
  begin

    select count(*) 
    into numReg 
    from acuerdopago 
    where idacuerdo = p_idAcuerdo; 
    
    if (numReg = 0 ) then
      p_codError := 1;
      p_msgError := 'Acuerdo inexistente';
      return;
    end if;
    
    if ( p_vigIni > p_vigFin ) then
      p_codError := 2;
      p_msgError := 'Fecha de vigencia errada';
      return;
    end if;
    
    select a.valor
    into vlrTotal 
    from acuerdopago a
    where idacuerdo = p_idAcuerdo; 
    
    if ( vlrTotal <> (p_vlrSimitAntes + p_vlrSimitDespues + p_vlrPolcaAntes + p_vlrPolcaDespues + p_vlrReintegro  ) ) then
      p_codError := 3;
      p_msgError := 'Valores de distribucion no concuerda con valor total';
      return;
    end if;
    
    select a.fechaliq 
    into fecLiq 
    from acuerdopago a
    where idacuerdo = p_idAcuerdo; 

    if ( not fecLiq is null ) then
      p_codError := 4;
      p_msgError := 'Acuerdo ya liquidado. Solicitar reproceso';
      return;
    end if;
    
    
    update acuerdopago
       set  vlrsimitantesprorroga = p_vlrSimitAntes,
           vlrpolcaantesprorroga = p_vlrPolcaAntes,
           vlrsimitdespuesprorroga = p_vlrSimitDespues,
           vlrpolcadespuesprorroga = p_vlrPolcaDespues,
           vlrreintegrarmun = p_vlrReintegro,
           vigencia = to_char(p_vigIni,'yyyy') || '-' || to_char(p_vigFin,'yyyy'),
           fec_vig_ini = p_vigIni,
           fec_vig_fin = p_vigFin,
           estado = p_estado,
           fechaestado = sysdate,
           usuarioestado = p_usuario,
           observaciones = p_observaciones,
           porcentaje = p_porcentajePolca
     where idacuerdo = p_idAcuerdo;
    

    commit;

    sp_log(p_usuario, 'act_acuerdo', to_char(p_idAcuerdo));
    commit;

    p_codError := 0;
    p_msgError := 'Operacion Correcta';

  EXCEPTION
    WHEN OTHERS THEN
      p_codError := SQLCODE;
      p_msgError := SQLERRM;

  END act_acuerdo;
end PK_ACUERDO;
/

