                                  p_error          OUT VARCHAR2) as

    w_fec_cor_ini     date;
    w_fec_cor_fin     date;

    v_cod_error number;

  begin

    select TO_DATE(p_feccor_ini_ano || '-' || p_feccor_ini_mes || '-' ||
                   p_feccor_ini_dia,
                   'YYYY-MM-DD')
      into w_fec_cor_ini
      from DUAL;

    select TO_DATE(p_feccor_fin_ano || '-' || p_feccor_fin_mes || '-' ||
                   p_feccor_fin_dia,
                   'YYYY-MM-DD')
      into w_fec_cor_fin
      from DUAL;

    BEGIN
      DBMS_SCHEDULER.drop_program_argument(program_name  => 'CARGUE_PROC_PROGRAMADO',
                                           argument_name => 'v_fec_ini');
    EXCEPTION
      WHEN OTHERS THEN
        v_cod_error := 0;
    end;

    BEGIN
      DBMS_SCHEDULER.drop_program_argument(program_name  => 'CARGUE_PROC_PROGRAMADO',
                                           argument_name => 'v_fec_fin');
    EXCEPTION
      WHEN OTHERS THEN
        v_cod_error := 0;
    end;

    BEGIN
      DBMS_SCHEDULER.drop_program_argument(program_name  => 'CARGUE_PROC_PROGRAMADO',
                                           argument_name => 'v_usuario');

    EXCEPTION
      WHEN OTHERS THEN
        v_cod_error := 0;
    end;

    BEGIN

      DBMS_SCHEDULER.drop_program(program_name => 'CARGUE_PROC_PROGRAMADO',
                                  force        => TRUE);

    EXCEPTION
      WHEN OTHERS THEN
        v_cod_error := 0;
    end;

    BEGIN
      DBMS_SCHEDULER.drop_job(job_name => 'CARGUE_TRABAJO_PLSQL',
                              force    => TRUE);
    EXCEPTION
      WHEN OTHERS THEN
        v_cod_error := 0;
    end;

    BEGIN
      DBMS_SCHEDULER.drop_schedule(schedule_name => 'CARGUE_SCHEDULE',
                                   force         => TRUE);
    EXCEPTION
      WHEN OTHERS THEN
        v_cod_error := 0;
    end;

    DBMS_SCHEDULER.create_program(program_name        => 'CARGUE_PROC_PROGRAMADO',
                                  program_type        => 'STORED_PROCEDURE',
                                  program_action      => 'PK_BATCH.enviar_proceso_cargue',
                                  number_of_arguments => 3,
                                  enabled             => FALSE,
                                  comments            => 'Proc. PLSQL programado');

    commit;

    DBMS_SCHEDULER.define_program_argument(program_name      => 'CARGUE_PROC_PROGRAMADO',
                                           argument_position => 1,
                                           argument_name     => 'v_fec_ini',
                                           argument_type     => 'VARCHAR2',
                                           default_value     => to_char(w_fec_cor_ini,
                                                                        'yyyy-mm-dd'));
    commit;

    DBMS_SCHEDULER.define_program_argument(program_name      => 'CARGUE_PROC_PROGRAMADO',
                                           argument_position => 2,
                                           argument_name     => 'v_fec_fin',
                                           argument_type     => 'VARCHAR2',
                                           default_value     => to_char(w_fec_cor_fin,
                                                                        'yyyy-mm-dd'));

    commit;

    DBMS_SCHEDULER.define_program_argument(program_name      => 'CARGUE_PROC_PROGRAMADO',
                                           argument_position => 3,
                                           argument_name     => 'v_usuario',
                                           argument_type     => 'VARCHAR2',
                                           default_value     => p_usuario);

    commit;

    DBMS_SCHEDULER.enable(name => 'CARGUE_PROC_PROGRAMADO');

    commit;

    DBMS_SCHEDULER.create_schedule(schedule_name   => 'CARGUE_SCHEDULE',
                                   start_date      => trunc(sysdate),
                                   repeat_interval => 'freq=MINUTELY;interval=1',
                                   comments        => 'Para ejecutar ya');

    commit;

    DBMS_SCHEDULER.create_job(job_name      => 'CARGUE_TRABAJO_PLSQL',
                              program_name  => 'CARGUE_PROC_PROGRAMADO',
                              schedule_name => 'CARGUE_SCHEDULE',
                              enabled       => FALSE,
                              comments      => 'Semanal a las 01:00');
    commit;

    DBMS_SCHEDULER.set_job_argument_value(job_name          => 'CARGUE_TRABAJO_PLSQL',
                                          argument_position => 1,
                                          argument_value    => to_char(w_fec_cor_ini,
                                                                       'yyyy-mm-dd'));
    commit;

    DBMS_SCHEDULER.set_job_argument_value(job_name          => 'CARGUE_TRABAJO_PLSQL',
                                          argument_position => 2,
                                          argument_value    => to_char(w_fec_cor_fin,
                                                                       'yyyy-mm-dd'));
    commit;


    DBMS_SCHEDULER.set_job_argument_value(job_name          => 'CARGUE_TRABAJO_PLSQL',
                                          argument_position => 3,
                                          argument_value    => p_usuario);

    commit;

    DBMS_SCHEDULER.enable('CARGUE_TRABAJO_PLSQL');

    commit;

    p_error := 'Proceso de carga de Recaudo Externo inicado. Favor esperar correo de confirmacion o verificar log';

    sp_log(p_usuario,
           'Carga Recaudo Externo Iniciado: ',to_char( w_fec_cor_ini, 'yyyy-mm-dd') || '-' || to_char( w_fec_cor_fin, 'yyyy-mm-dd'));

  EXCEPTION
    WHEN OTHERS THEN
      sp_ex(SQLCODE,
            p_usuario || ' [inicar_proceso_deuda] ' || SQLERRM,
            p_error);

  END inicar_proceso_cargue;




