  Procedure procesarControl(cuenta      varchar2,
                            indTipo     varchar2,
                            v_fecTrf    date,
                            v_idbanco   varchar2,
                            v_cuenta    out varchar2,
                            v_autoridad out varchar2,
                            v_fecha     out varchar2,
                            v_numero    out varchar2,
                            v_valor     out varchar2,
                            v_estado    out varchar2,
                            v_proceso   out varchar2) as
  begin
    v_cuenta    := cuenta;
    v_autoridad := indTipo;
    v_fecha     := to_char(v_fecTrf, 'yyyy-MM-dd');
    v_estado    := 'Correcto';
    v_proceso   := '';

    begin
      select to_char(c.nrotrn), to_char(c.valortrf, '9999999999999999.99')
        into v_numero, v_valor
        from control_envio c
       where c.idbanco = v_idbanco
         and c.fechatrf = v_fecTrf
         and c.cuentaorigen = cuenta;
    Exception
      WHEN OTHERS THEN
        v_numero := '0';
        v_valor  := '0.0';
    end;

    begin
      select c.nroproceso
        into v_proceso
        from control_envio_reg c
       where c.idbanco = v_idbanco
         and c.fechatrf = v_fecTrf
         and c.cuentaorigen = cuenta;
    Exception
      WHEN OTHERS THEN
        v_proceso := '';
    end;

  end procesarControl;

