          IF p_estado = 'ANULADA' THEN
            update Novdevolucion
               set estadonov       = p_estado,
                   usuarioanul     = p_usuario,
                   fechaanul       = sysdate,
                   fechaAplicacion = null
             where tiponov = p_tiponov
               and numeronov = to_number(p_numeronov);
          ELSE
            update Novdevolucion
               set estadonov = p_estado
             where tiponov = p_tiponov
               and numeronov = to_number(p_numeronov);
          END IF;
        END IF;
      
        IF p_estado = 'ANULADA' THEN
