        select seq_novedad.Nextval into numNovedad from dual;

        select COUNT(*)
          INTO v_valida
          from municipio
         where idmunicipio = p_idmunicipio;

        if (v_valida > 0) then

          select nombre
            into p_nom
            from municipio
           where idmunicipio = p_idmunicipio;

        end if;

        if (v_valida = 0) then

          select max(nombre)
            into p_nom
            from novmunicipio
           where idmunicipio = p_idmunicipio;

        end if;

        insert into novdias_dispersion
          (idmunicipio,
           idbanco,
           dias_dispersion_sl,
           frec_dispersion_sl,
           dias_dispersion_pl,
           frec_dispersion_pl,
