           w_fecha_act,
           p_firma1,
           p_firma2,
           'INGRESADA',
           p_dnsAvvillas,
           p_dnsGranbanco,
           p_nom);

        select COUNT(*)
          INTO v_valida
          from municipio
         where idmunicipio = p_idmunicipio;

        if (v_valida = 0) then

          insert into municipio
            (idmunicipio, nombre, iddepartamento)
          values
