               p_dias_dispersion_pe || '-' || p_frec_dispersion_pe || '-' ||
               p_tiponov || '-' || numNovedad || '-' || p_usuario || '-' ||
               w_fecha_act || '-' || p_firma1 || '-' || p_firma2);
        commit;

        p_error   := MSG_INS_OK;
        p_novedad := p_tiponov || '-' || to_char(numNovedad, '0000000');

        sp_envionotificacion(gruporecibe => 4,
