            /*and a.codigo_banco in (50,52)*/
         and a.fecha_contable >= w_fec_cor_ini
         and a.fecha_contable <= w_fec_cor_fin;
    w_registros := SQL%ROWCOUNT;

    insert into recaudo_externo
