       medioImposicion)
      SELECT SEQ_RECAUDO_EXTERNO.NEXTVAL,
             SUBSTR(b.cod_banco, 1, 2) cod_banco,
             --SUBSTR(b.cuenta, 1, 12) cuenta,
             case
               when length(cuenta) = 17 then
                SUBSTR(b.cuenta, 9, 9)
               when length(cuenta) = 9 then
                b.cuenta
               when length(cuenta) = 8 then
                '0' || b.cuenta
               else
