      commit;

      sp_envioNotificacion(grupoRecibe => 8,
                           grupoCopia  => 2,
                           asunto      => 'SDF - Proceso de cargue dia: ' ||
                                          to_char(w_fec_cor_ini,
                                                  'yyyy-MM-dd') || '-' ||
                                          to_char(w_fec_cor_fin,
                                                  'yyyy-mm-dd'),
