           (select (case
                     when l.indtipo = 'SIMIT' then
                      dd.dias_dispersion_se
                     else
                      dd.dias_dispersion_pe
                   end),
                   (case
                     when l.indtipo = 'SIMIT' then
                      dd.frec_dispersion_se
                     else
                      dd.frec_dispersion_pe
                   end)
              from dias_dispersion dd
             where dd.idmunicipio = l.idmunicipio
