       and l.validador_fecha = '000'
       and l.ind_proceso = 1;

    update recaudo_externo_liquidacion l
       set (l.p_valor_22) =
           (select l.p_valor_21 *
                   ((v.p_fcm_contravencional + v.p_conces_contravencional) / 100) / 0.45
              from validador_fecha v
             where v.idmunicipio = l.idmunicipio
