       set (l.p_valor_23,
            l.p_valor_27,
            l.p_valor_28,
            l.p_valor_29,
            l.p_valor_30,
            p_valor_44) =
           (select l.p_valor_23 -
                   ((l.p_valor_24 + l.p_valor_25) * fcm.iva / 100),

                   (l.p_valor_24 + l.p_valor_25) * (fcm.iva / 100),

                   (l.p_valor_24 + l.p_valor_25) * (0 / 100), -- timbre contravencional en 0.0 debe ser fcm.impuesto_timbre_fcm

