       s_valor_3,
       s_valor_4,
       s_valor_5,
       s_valor_6,
       p_valor_7,
       p_valor_8,
       p_valor_9,
       p_valor_10,
       p_valor_11,
       p_valor_12,
       p_valor_13,
       p_valor_14,
       p_valor_15,
       p_valor_16,
       p_valor_17,
       p_valor_18,
       p_valor_19,
       p_valor_20,
       p_valor_21,
       p_valor_22,
       p_valor_23,
       p_valor_24,
       p_valor_25,
       p_valor_26,
       p_valor_27,
       p_valor_28,
       p_consignado,
       idrecaudolocal_liquidacion,
       nit_concesionario,
       nit_fondo)
      select seq_recaudo_local_liquidacion.nextval,
             a.idbanco,
             a.cta_recaudo,
