       and r.ind_base_ter1 > 0
       and r.ind_proceso = 1;
  
    update recaudo_externo_capital r
       set nit_tercero2 =
           (select nit_t
              from tercero t
             where t.idmunicipio = r.idmunicipio
               and t.num_tercero = 2)
     where fec_corte >= w_fec_cor_ini
       and fec_corte <= w_fec_cor_fin
       and r.ind_base_ter2 > 0
       and r.ind_proceso = 1;
  
    update recaudo_externo_capital r
       set nit_tercero3 =
