           (select t.nombre
              from tercero t
             where t.idmunicipio = r.idmunicipio
               and t.num_tercero = 2),
           r.id_concesionario_pro = '98'
     where r.concepto = 'Tercero 2'
       and fec_corte_ini >= w_fec_cor_ini
       and fec_corte_fin <= w_fec_cor_fin;
  
    update recaudo_externo_disp_cap r
       set r.concepto            =
           (select t.nombre
              from tercero t
             where t.idmunicipio = r.idmunicipio
               and t.num_tercero = 3),
           r.id_concesionario_pro = '98'
     where r.concepto = 'Tercero 3'
       and fec_corte_ini >= w_fec_cor_ini
       and fec_corte_fin <= w_fec_cor_fin;
  
    update recaudo_externo_disp_cap r
       set r.concepto            =
           (select t.nombre
              from tercero t
             where t.idmunicipio = r.idmunicipio
               and t.num_tercero = 4),
           r.id_concesionario_pro = '98'
     where r.concepto = 'Tercero 4'
       and fec_corte_ini >= w_fec_cor_ini
       and fec_corte_fin <= w_fec_cor_fin;
  
    commit;
  
    insert into recaudo_externo_disp_cap
      (fec_corte_ini,
       fec_corte_fin,
       fec_dispersion,
       id_concesionario_liq,
       orden,
       concepto,
       ori_idbanco,
       ori_tipocuenta,
       ori_cuenta,
