  PROCEDURE aplicaTecho_externo(v_fechaCorte date,
                                v_usuario    varchar2,
                                v_cod_error  out number,
                                v_msg_error  out varchar2) as
    idConcesionario char(2);
  
    CURSOR tConcesionario IS
      select c.idconcesionario, c.fechaprorroga
        from p_dispersion_concesionario c
       where not c.fechaprorroga is null
         and c.idconcesionario <> '03';
  
    CURSOR tLiquidacion IS
      select r.*
        from recaudo_externo_liquidacion r
       where r.fec_corte = v_fechaCorte
         and r.id_concesionario_liq = idConcesionario
       order by r.indtipo, r.num_liquidacion;
  
    vlrTecho  number;
    vlrPagCon number;
  
    vlrPagLiq number;
  
    vlrPagDet number;
  
  BEGIN
    -- poner a ceros fondo renovacion
    update recaudo_externo_liquidacion r
       set r.fondorenovacion = 0,
           r.fondorenov27    = 0,
           r.fondorenov3     = 0,
           r.fondorenov35    = 0,
           r.fondorenov7     = 0
     where r.fec_corte = v_fechacorte
       and r.fondorenovacion is null;
  
    -- para cada concesionario con prorroga (ciclo1)
    for conces in tConcesionario loop
    
      idConcesionario := conces.idconcesionario;
    
      -- Si la fecha de corte a procesar es >= prorroga (if1) se verifica distribucion
      if v_fechaCorte >= conces.fechaprorroga then
      
        -- obtener valor del techo para el periodo del concesionario
        select t.valortecho
          into vlrTecho
          from controltecho t
         where t.idconcesionario = conces.idconcesionario
           and t.periodo = to_char(v_fechaCorte, 'yyyymm')
           and t.tiporecaudo = 'EXT';
      
        -- Limpiar procesos anteriores
        delete techoutilizado
         where idconcesionario = conces.idconcesionario
           and tiporecaudo = 'EXT'
           and fechacorte = v_fechacorte;
      
        -- valor pagado al concesionario en el periodo (if2)
        select nvl(sum(t.valorsimit7 + t.valorpolca35), 0)
          into vlrPagCon
          from techoutilizado t
         where idconcesionario = conces.idconcesionario
           and tiporecaudo = 'EXT'
           and to_char(fechacorte, 'yyyymm') =
               to_char(v_fechacorte, 'yyyymm');
      
        -- Si ya se cumplio el techo ajustar distribucion para transferir a fondo
        -- de renovacion if3
        if vlrPagCon >= vlrTecho then
        
          -- mover valor concesionario a fondo renovacion (para recaudo simit)
          update recaudo_externo_liquidacion r
             set r.fondorenovacion = s_valor_4,
                 r.fondorenov27    = 0,
                 r.fondorenov3     = 0,
                 r.fondorenov35    = 0,
                 r.fondorenov7     = s_valor_4,
                 r.s_valor_4       = 0,
                 r.s_valor_28      = 0
           where r.fec_corte = v_fechacorte
             and r.id_concesionario_liq = conces.idconcesionario
             and r.indtipo = 'SIMIT';
        
          -- mover valor concesionario a fondo renovacion (para recaudo polca)
          update recaudo_externo_liquidacion r
             set r.fondorenovacion = p_valor_4,
                 r.fondorenov27    = 0,
                 r.fondorenov3     = 0,
                 r.fondorenov35    = p_valor_4,
                 r.fondorenov7     = 0,
                 r.p_valor_4       = 0,
                 r.p_valor_41      = 0
           where r.fec_corte = v_fechacorte
             and r.id_concesionario_liq = conces.idconcesionario
             and r.indtipo = 'POLCA';
        
        end if; -- if3
      
        if vlrPagCon < vlrTecho then
          -- if5
        
          -- calcular valor a pagar de la distribucion
          -- el valor a pagar por simit es 7% y por polca 3.5%
          select nvl(sum(s_valor_4 + p_valor_4), 0)
            into vlrPagLiq
            from recaudo_externo_liquidacion r
           where r.fec_corte = v_fechacorte
             and r.id_concesionario_liq = conces.idconcesionario;
        
          -- Si lo que se ha pagado + lo que va a pagar hace que se supere el techo
          -- se debe ajustar liquidaciones hasta llegar al techo
        
          if ((vlrPagCon + vlrPagLiq) > vlrTecho) then
            -- if7
          
            -- para cada liquidacion (ciclo2)
            for liq in tLiquidacion loop
            
              -- obtener el valor a pagar por la liquidacion al concesionario
              vlrPagDet := liq.s_valor_4 + liq. p_valor_4;
            
              -- lo que se ha pagado ya supero el techo
              if (vlrPagCon >= vlrTecho) then
                -- if8
                if liq.indtipo = 'SIMIT' then
                  -- if9
                  -- mover valor concesionario a fondo renovacion (para recaudo simit)
                  update recaudo_externo_liquidacion r
                     set r.fondorenovacion = s_valor_4,
                         r.fondorenov27    = 0,
                         r.fondorenov3     = 0,
                         r.fondorenov35    = 0,
                         r.fondorenov7     = s_valor_4,
                         r.s_valor_4       = 0,
                         r.s_valor_28      = 0
                   where r.fec_corte = v_fechacorte
                     and r.num_liquidacion = liq.num_liquidacion;
                else
                  -- mover valor concesionario a fondo renovacion (para recaudo polca)
                  update recaudo_externo_liquidacion r
                     set r.fondorenovacion = p_valor_4,
                         r.fondorenov27    = 0,
                         r.fondorenov3     = 0,
                         r.fondorenov35    = p_valor_4,
                         r.fondorenov7     = 0,
                         r.p_valor_4       = 0,
                         r.p_valor_41      = 0
                   where r.fec_corte = v_fechacorte
                     and r.num_liquidacion = liq.num_liquidacion;
                end if; -- end if9
              
              else
                if ((vlrPagCon + vlrPagDet) >= vlrTecho) then
                  --if10
                
                  if liq.indtipo = 'SIMIT' then
                    -- if11
                    update recaudo_externo_liquidacion r
                       set r.fondorenovacion = vlrPagCon + s_valor_4 -
                                               vlrTecho,
                           r.fondorenov27    = 0,
                           r.fondorenov3     = 0,
                           r.fondorenov35    = 0,
                           r.fondorenov7     = vlrPagCon + s_valor_4 -
                                               vlrTecho,
                           r.s_valor_4       = vlrTecho - vlrPagCon,
                           r.s_valor_28      = vlrTecho - vlrPagCon
                     where r.fec_corte = v_fechacorte
                       and r.num_liquidacion = liq.num_liquidacion;
                  else
                    -- si se pasa el techo por el 3.5
                    -- mover valor concesionario a fondo renovacion (para recaudo polca)
                    update recaudo_externo_liquidacion r
                       set r.fondorenovacion = vlrPagCon + p_valor_4 -
                                               vlrTecho,
                           r.fondorenov27    = 0,
                           r.fondorenov3     = 0,
                           r.fondorenov35    = vlrPagCon + p_valor_4 -
                                               vlrTecho,
                           r.fondorenov7     = 0,
                           r.p_valor_4       = vlrTecho - vlrPagCon,
                           r.p_valor_41      = vlrTecho - vlrPagCon
                     where r.fec_corte = v_fechacorte
                       and r.num_liquidacion = liq.num_liquidacion;
                  
                  end if; --if11
                end if; --if10
              end if; -- end if8
            
              vlrPagCon := vlrPagCon + vlrPagDet;
            
            end loop; -- fin ciclo2
          
          end if; -- if7
        
          -- Si lo que se va a pagar mas lo que se lleva pagado es menor a techo
          -- no afectar distribucion
          --if ((vlrPagCon + vlrPagLiq) < vlrTecho) then
          --  no hacer nada
          --end if;
        
        end if; -- if5
      
        -- actualizar control de techo
        insert into techoutilizado
          (idconcesionario,
           tiporecaudo,
           fechacorte,
           valorsimit7,
           valorpolca35,
           valorsevial3,
           valorsevial27,
           valorhaciafondo,
           valordesdefondo,
           valortecho)
          select r.id_concesionario_liq,
                 'EXT',
                 max(r.fec_corte),
                 nvl(sum(r.s_valor_4), 0), -- valorsimit7
                 nvl(sum(p_valor_4), 0), -- valorpolca35
                 0, -- valorsevial3
                 0, -- valorsevial27
                 nvl(sum(r.fondorenovacion), 0), --valorhaciafondo
                 0, --valordesdefondo
                 vlrTecho -- valortecho
            from recaudo_externo_liquidacion r
           where r.fec_corte = v_fechacorte
             and r.id_concesionario_liq = conces.idconcesionario
           group by r.id_concesionario_liq;
      
        commit;
      
      end if; -- fin if1
    
      commit;
    
    end loop; -- fin ciclo1
  
    v_cod_error := 0000;
    v_msg_error := 'Aplicacion de techo correcta';
  
  EXCEPTION
    WHEN OTHERS THEN
      v_cod_error := SQLCODE;
      v_msg_error := SQLERRM;
    
  END aplicaTecho_externo;

  PROCEDURE aplicaTecho_externoSevial(v_fechaCorte date,
                                      v_usuario    varchar2,
                                      v_cod_error  out number,
                                      v_msg_error  out varchar2) as
    idConcesionario char(2);
  
    CURSOR tConcesionario IS
      select c.idconcesionario, c.fechaprorroga
        from p_dispersion_concesionario c
       where not c.fechaprorroga is null
         and c.idconcesionario = '03';
  
    CURSOR tLiquidacionPolca IS
      select r.*
        from recaudo_externo_liquidacion r
       where r.fec_corte = v_fechaCorte
         and r.indtipo = 'POLCA'
       order by r.id_concesionario_liq, r.num_liquidacion;
  
    CURSOR tLiquidacionPolcaSevial IS
      select r.*
        from recaudo_externo_liquidacion r
       where r.fec_corte = v_fechaCorte
         and r.indtipo = 'POLCA'
         and r.id_concesionario_liq = idConcesionario
       order by r.id_concesionario_liq, r.num_liquidacion;
  
    CURSOR tLiquidacionSimit IS
      select r.*
        from recaudo_externo_liquidacion r
       where r.fec_corte = v_fechaCorte
         and r.indtipo = 'SIMIT'
         and r.id_concesionario_liq = idConcesionario
       order by r.id_concesionario_liq, r.num_liquidacion;
  
    vlrTecho  number;
    vlrPagCon number;
    vlrPagSev number;
  
    vlrPagLiq    number;
    vlrPagLiqSev number;
  
    vlrPagDet    number;
    vlrPagDetSev number;
  
    vlrPagLiqSev27 number;
    vlrPagLiqSev3  number;
    vlrPagLiqSev35 number;
  
    valorHaciaFondo number;
    vlrFon          number;
  
  BEGIN
    -- poner a ceros fondo renovacion
    update recaudo_externo_liquidacion r
       set r.fondorenovacion = 0,
           r.fondorenov27    = 0,
           r.fondorenov3     = 0,
           r.fondorenov35    = 0,
           r.fondorenov7     = 0
     where r.fec_corte = v_fechacorte
       and r.fondorenovacion is null;
  
    -- para cada concesionario con prorroga (ciclo1)
    for conces in tConcesionario loop
    
      idConcesionario := conces.idconcesionario;
      valorHaciaFondo := 0;
    
      -- Si la fecha de corte a procesar es >= prorroga (if1) se verifica distribucion
      if v_fechaCorte >= conces.fechaprorroga then
      
        -- obtener valor del techo para el periodo del concesionario
        select t.valortecho
          into vlrTecho
          from controltecho t
         where t.idconcesionario = conces.idconcesionario
           and t.periodo = to_char(v_fechaCorte, 'yyyymm')
           and t.tiporecaudo = 'EXT';
      
        -- Limpiar procesos anteriores
        delete techoutilizado
         where idconcesionario = conces.idconcesionario
           and tiporecaudo = 'EXT'
           and fechacorte = v_fechacorte;
      
        -- valor pagado al concesionario en el periodo (if2)
        select nvl(sum(t.valorsimit7 + t.valorpolca35 + -- t.valorsevial3 +
                       t.valorsevial27),
                   0)
          into vlrPagCon
          from techoutilizado t
         where idconcesionario = conces.idconcesionario
           and tiporecaudo = 'EXT'
           and to_char(fechacorte, 'yyyymm') =
               to_char(v_fechacorte, 'yyyymm');
      
        -- Si ya se cumplio el techo ajustar distribucion para transferir a fondo
        -- de renovacion if3
        if vlrPagCon >= vlrTecho then
        
          -- mover valor concesionario a fondo renovacion (para recaudo simit)
          select nvl(sum(r.s_valor_4), 0)
            into vlrFon
            from recaudo_externo_liquidacion r
           where r.fec_corte = v_fechacorte
             and r.id_concesionario_liq = conces.idconcesionario
             and r.indtipo = 'SIMIT';
        
          valorHaciaFondo := valorHaciaFondo + VlrFon;
        
          update recaudo_externo_liquidacion r
             set r.fondorenovacion = s_valor_4,
                 r.fondorenov27    = 0,
                 r.fondorenov3     = 0,
                 r.fondorenov35    = 0,
                 r.fondorenov7     = s_valor_4
           where r.fec_corte = v_fechacorte
             and r.id_concesionario_liq = conces.idconcesionario
             and r.indtipo = 'SIMIT';
        
          update recaudo_externo_liquidacion r
             set r.s_valor_4 = 0, r.s_valor_28 = 0
           where r.fec_corte = v_fechacorte
             and r.id_concesionario_liq = conces.idconcesionario
             and r.indtipo = 'SIMIT';
        
