            into vlrFon
            from recaudo_externo_liquidacion r
           where r.fec_corte = v_fechacorte
             and r.id_concesionario_liq = conces.idconcesionario
             and r.indtipo = 'POLCA';
        
          valorHaciaFondo := valorHaciaFondo + VlrFon;
        
          update recaudo_externo_liquidacion r
             set r.fondorenovacion = p_valor_4, r.fondorenov35 = p_valor_4
           where r.fec_corte = v_fechacorte
             and r.id_concesionario_liq = conces.idconcesionario
             and r.indtipo = 'POLCA';
        
          update recaudo_externo_liquidacion r
             set r.p_valor_4 = 0, r.p_valor_41 = 0
           where r.fec_corte = v_fechacorte
             and r.id_concesionario_liq = conces.idconcesionario
             and r.indtipo = 'POLCA';
        
          -- lo pagado a sevial por convenio polca (3%)
          select nvl(sum(r.p_valor_9), 0)
            into vlrFon
            from recaudo_externo_liquidacion r
           where r.fec_corte = v_fechacorte
             and r.indtipo = 'POLCA';
        
          valorHaciaFondo := valorHaciaFondo + VlrFon;
        
      /*   el 3% ya no es parte del techo
       update recaudo_externo_liquidacion r
             set r.fondorenovacion = r.fondorenovacion + r.p_valor_9,
                 r.fondorenov3     = r.p_valor_9
           where r.fec_corte = v_fechacorte
