          -- no afectar distribucion
        
          -- Si lo que se ha pagado + lo que va a pagar hace que se supere el techo
          -- se debe ajustar liquidaciones hasta llegar al techo
          -- if7
          if ((vlrPagCon + vlrPagLiq) > vlrTecho) then
          
            -- 2,7 ya no es parte del techo
          
            -- el valor a pagar por 2.7% (sin impuestos)
           /* select nvl(sum((p_valor_14 + p_valor_19 - p_valor_18)), 0)
              into vlrPagLiqSev27
              from recaudo_externo_liquidacion r
