                   and r.indtipo = 'POLCA';
              
                valorHaciaFondo := valorHaciaFondo + vlrFon;
              
                update recaudo_externo_liquidacion r
                   set r.fondorenovacion = p_valor_4,
                       r.fondorenov35    = p_valor_4
                 where r.fec_corte = v_fechacorte
                   and r.id_concesionario_liq = conces.idconcesionario
                   and r.indtipo = 'POLCA';
              
                update recaudo_externo_liquidacion r
                   set r.p_valor_4 = 0, r.p_valor_41 = 0
                 where r.fec_corte = v_fechacorte
                   and r.id_concesionario_liq = conces.idconcesionario
                   and r.indtipo = 'POLCA';
              
              else
              
                -- mantener 2.7% y 3%, validar 3.5% de concesionario
                select nvl(sum(p_valor_4), 0)
                  into vlrPagLiqSev35
                  from recaudo_externo_liquidacion r
                 where r.fec_corte = v_fechacorte
                   and r.id_concesionario_liq = conces.idconcesionario;
              
                -- si el valor del 3.5% hace que se pase del techo --if10
                -- 2,7 ya no es techo                if ((vlrPagCon + vlrPagLiqSev27 + vlrPagLiqSev3 +
                if ((vlrPagCon + vlrPagLiqSev3 + vlrPagLiqSev35) > vlrTecho) then
                
                  -- pagar 3.5% hasta que se cumpla techo y pasar a fondo demas recursos
                  --                  vlrPagCon := vlrPagCon + vlrPagLiqSev27 + vlrPagLiqSev3;
                  vlrPagCon := vlrPagCon + vlrPagLiqSev3;
                
                  for liq in tLiquidacionPolcaSevial loop
                    -- valor a pagar por liquidacion
                    vlrPagDet := liq.p_valor_4;
                  
                    -- lo que se ha pagado ya supero el techo --if11
                    if (vlrPagCon >= vlrTecho) then
                    
                      -- mover valor pago a sevial directamente a fondo renovacion
                      select nvl(sum(p_valor_4), 0)
                        into vlrFon
                        from recaudo_externo_liquidacion r
                       where r.fec_corte = v_fechacorte
                         and r.num_liquidacion = liq.num_liquidacion;
                    
                      valorHaciaFondo := valorHaciaFondo + vlrFon;
                    
                      update recaudo_externo_liquidacion r
                         set r.fondorenovacion = p_valor_4,
                             r.fondorenov35    = p_valor_4
                       where r.fec_corte = v_fechacorte
                         and r.num_liquidacion = liq.num_liquidacion;
                    
                      update recaudo_externo_liquidacion r
                         set r.p_valor_4 = 0, r.p_valor_41 = 0
                       where r.fec_corte = v_fechacorte
                         and r.num_liquidacion = liq.num_liquidacion;
                    else
                      if (vlrPagCon + vlrPagDet >= vlrTecho) then
                      
                        select nvl(sum(p_valor_4), 0)
                          into vlrFon
                          from recaudo_externo_liquidacion r
                         where r.fec_corte = v_fechacorte
                           and r.num_liquidacion = liq.num_liquidacion;
                      
                        valorHaciaFondo := valorHaciaFondo + vlrPagCon +
                                           vlrFon - vlrTecho;
                      
                        update recaudo_externo_liquidacion r
                           set r.fondorenovacion = vlrPagCon + p_valor_4 -
                                                   vlrTecho,
                               r.fondorenov35    = vlrPagCon + p_valor_4 -
                                                   vlrTecho
                         where r.fec_corte = v_fechacorte
                           and r.num_liquidacion = liq.num_liquidacion;
                      
                        update recaudo_externo_liquidacion r
                           set r.p_valor_4  = vlrTecho - vlrPagCon,
                               r.p_valor_41 = vlrTecho - vlrPagCon
                         where r.fec_corte = v_fechacorte
                           and r.num_liquidacion = liq.num_liquidacion;
                      
                      end if;
                    
                    end if; -- if11
                  
                    vlrPagCon := vlrPagCon + vlrPagDet;
                  end loop; -- fin ciclo2
                
                  -- mover valor concesionario a fondo renovacion (para recaudo simit)
                  select nvl(sum(s_valor_4), 0)
                    into vlrFon
                    from recaudo_externo_liquidacion r
                   where r.fec_corte = v_fechacorte
                     and r.id_concesionario_liq = conces.idconcesionario
                     and r.indtipo = 'SIMIT';
                
                  valorHaciaFondo := valorHaciaFondo + vlrFon;
                
                  update recaudo_externo_liquidacion r
                     set r.fondorenovacion = s_valor_4,
                         r.fondorenov7     = s_valor_4
                   where r.fec_corte = v_fechacorte
                     and r.id_concesionario_liq = conces.idconcesionario
                     and r.indtipo = 'SIMIT';
                
                  update recaudo_externo_liquidacion r
                     set r.s_valor_4 = 0, r.s_valor_28 = 0
                   where r.fec_corte = v_fechacorte
                     and r.id_concesionario_liq = conces.idconcesionario
                     and r.indtipo = 'SIMIT';
                
                else
                  -- mantener 2,7%, 3%, 3.5% sin modificacion y pagar 7% hasta cumplir techo
                  -- para cada liquidacion simit de sevial pagar hasta cumplir techo
                
                  --                  vlrPagCon := vlrPagCon + vlrPagLiqSev27 + vlrPagLiqSev3 +
                  vlrPagCon := vlrPagCon + vlrPagLiqSev3 + vlrPagLiqSev35;
                
                  for liq in tLiquidacionSimit loop
                    -- valor a pagar por liquidacion
                    vlrPagDet := liq.s_valor_4;
                  
                    -- lo que se ha pagado ya supero el techo -- if11
                    if (vlrPagCon >= vlrTecho) then
                    
                      select nvl(sum(s_valor_4), 0)
                        into vlrFon
                        from recaudo_externo_liquidacion r
                       where r.fec_corte = v_fechacorte
                         and r.num_liquidacion = liq.num_liquidacion;
                    
                      valorHaciaFondo := valorHaciaFondo + vlrFon;
                    
                      update recaudo_externo_liquidacion r
                         set r.fondorenovacion = s_valor_4,
                             r.fondorenov7     = s_valor_4
                       where r.fec_corte = v_fechacorte
                         and r.num_liquidacion = liq.num_liquidacion;
                    
                      update recaudo_externo_liquidacion r
                         set r.s_valor_4 = 0, r.s_valor_28 = 0
                       where r.fec_corte = v_fechacorte
                         and r.num_liquidacion = liq.num_liquidacion;
                    
                    else
                      if (vlrPagCon + vlrPagDet >= vlrTecho) then
                      
                        select nvl(sum(s_valor_4), 0)
                          into vlrFon
                          from recaudo_externo_liquidacion r
                         where r.fec_corte = v_fechacorte
                           and r.num_liquidacion = liq.num_liquidacion;
                      
                        valorHaciaFondo := valorHaciaFondo + vlrPagCon +
                                           vlrFon - vlrTecho;
                      
                        update recaudo_externo_liquidacion r
                           set r.fondorenovacion = vlrPagCon + s_valor_4 -
                                                   vlrTecho,
                               r.fondorenov7     = vlrPagCon + s_valor_4 -
                                                   vlrTecho
                         where r.fec_corte = v_fechacorte
                           and r.num_liquidacion = liq.num_liquidacion;
                      
                        update recaudo_externo_liquidacion r
                           set r.s_valor_4  = vlrTecho - vlrPagCon,
                               r.s_valor_28 = vlrTecho - vlrPagCon
                         where r.fec_corte = v_fechacorte
                           and r.num_liquidacion = liq.num_liquidacion;
                      
                      end if;
                    end if; -- if11
                    vlrPagCon := vlrPagCon + vlrPagDet;
                  end loop; -- fin ciclo2
                
                end if; -- if10
              end if; -- if9
           -- end if; -- if8
          end if; -- if7
        end if; -- if5
      
        -- actualizar control de techo valor 7% y valor 3.5%
        insert into techoutilizado
          (idconcesionario,
           tiporecaudo,
           fechacorte,
           valorsimit7,
           valorpolca35,
           valorsevial3,
           valorsevial27,
           valorhaciafondo,
           valordesdefondo,
           valortecho)
          select r.id_concesionario_liq,
                 'EXT',
                 max(r.fec_corte),
                 nvl(sum(r.s_valor_4), 0), -- valorsimit7
                 nvl(sum(p_valor_4), 0), -- valorpolca35
                 0, -- valorsevial3
                 0, -- valorsevial27
                 valorHaciaFondo, --valorhaciafondo
                 0, --valordesdefondo
                 vlrTecho -- valortecho
            from recaudo_externo_liquidacion r
           where r.fec_corte = v_fechacorte
             and r.id_concesionario_liq = conces.idconcesionario
           group by r.id_concesionario_liq;
      
        -- obtener valor del 3% y 2.7% de toda la liquidacion
        /*        update techoutilizado
          set (valorsevial3, valorsevial27) =
              (select nvl(sum(p_valor_9), 0),
                      nvl(sum(p_valor_14 + p_valor_19 - p_valor_18), 0)
                 from recaudo_externo_liquidacion r
                where r.fec_corte = v_fechacorte)
        where idconcesionario = conces.idconcesionario
          and tiporecaudo = 'EXT'
          and fechacorte = v_fechacorte;*/
      
        update techoutilizado
           set (valorsevial3) =
               (select nvl(sum(p_valor_9), 0)
                  from recaudo_externo_liquidacion r
                 where r.fec_corte = v_fechacorte)
         where idconcesionario = conces.idconcesionario
           and tiporecaudo = 'EXT'
           and fechacorte = v_fechacorte;
      
        commit;
      
      end if; -- fin if1
    
    end loop; -- fin ciclo1
  
    v_cod_error := 0000;
    v_msg_error := 'Aplicacion de techo correcta';
  
  EXCEPTION
    WHEN OTHERS THEN
      v_cod_error := SQLCODE;
      v_msg_error := SQLERRM;
    
  END aplicaTecho_externoSevial;

  PROCEDURE obtieneFondosTecho(v_fechaCorte date,
                               v_usuario    varchar2,
                               v_cod_error  out number,
                               v_msg_error  out varchar2) as
  
    fechaSiguiente date;
  
    idConc char(2);
  
    CURSOR tConcesionario IS
      select c.idconcesionario, c.fechaprorroga
        from p_dispersion_concesionario c
       where not c.fechaprorroga is null;
  
    vlrTecho number;
  
    vlrPagCon number;
  
    v_idbanco char(2);
    v_tipocta char(3);
    v_tipoide char(3);
    v_numide  varchar2(16);
    v_numcta  varchar2(250);
  
    v_fec_dispersion_con date;
    v_nomConcesionario   varchar2(250);
  
    v_idbanco_c    char(2);
    v_tipocuenta_c char(3);
    v_cuenta_c     varchar2(250);
    v_nit_c        varchar2(10);
  
  BEGIN
  
    fechaSiguiente := v_fechaCorte + 1;
  
    if (to_char(v_fechaCorte, 'MM') = to_char(fechaSiguiente, 'MM')) then
      v_cod_error := 0000;
      v_msg_error := 'No hay cambio de mes';
      return;
    end if;
  
    -- obtener datos de cuenta de fondo renovacion
    select idbanco, tipocta, tipoide, numide, desencriptar(numcta)
      into v_idbanco, v_tipocta, v_tipoide, v_numide, v_numcta
      from fondocont;
  
    -- para cada concesionario con prorroga (ciclo1)
    for conces in tConcesionario loop
    
      idConc := conces.idconcesionario;
    
      -- Si la fecha de corte a procesar es >= prorroga (if1) se verifica distribucion
      if (to_char(v_fechaCorte, 'yyyymm') >=
         to_char(conces.fechaprorroga, 'yyyymm')) then
      
        -- obtener fecha maxima de pago a concesionario
        select max(r.fec_dispersion_con)
          into v_fec_dispersion_con
          from recaudo_externo_liquidacion r
         where r.id_concesionario_liq = idConc
           and to_char(r.fec_corte, 'yyyymm') =
               to_char(v_fechaCorte, 'yyyymm');
      
        -- nombre de concesionario
        select c.nombre
          into v_nomConcesionario
          from concesionario c
         where c.idconcesionario = idConc;
      
        -- cuenta del concesionario
        select idbanco, tipocuenta, cuenta, nit_c
          into v_idbanco_c, v_tipocuenta_c, v_cuenta_c, v_nit_c
          from cta_dispersion_concesionario c
         where c.idconcesionario = idConc
           and c.idconcepto = 1
           and c.idbancorec = '52';
      
        -- obtener valor del techo para el periodo del concesionario
        select t.valortecho
          into vlrTecho
          from controltecho t
         where t.idconcesionario = conces.idconcesionario
           and t.periodo = to_char(v_fechaCorte, 'yyyymm')
           and t.tiporecaudo = 'EXT';
      
        -- valor pagado al concesionario en el periodo (if2)
        select nvl(sum(t.valorsimit7 + t.valorpolca35 + t.valorsevial3 +
                       t.valorsevial27),
                   0)
          into vlrPagCon
          from techoutilizado t
         where idconcesionario = conces.idconcesionario
           and tiporecaudo = 'EXT'
           and to_char(fechacorte, 'yyyymm') =
               to_char(v_fechacorte, 'yyyymm');
      
        /*        -- Si no se cumplio el techo ajustar distribucion para transferir desde fondo
                -- de renovacion a concesionario if2
                if vlrPagCon < vlrTecho then
                
                  -- valor girado desde el fondo al concesioanrio
                  insert into recaudo_externo_disp
                    (fec_corte_ini,
                     fec_corte_fin,
                     fec_dispersion,
                     id_concesionario_liq,
                     orden,
                     valor,
                     concepto,
                     ori_idbanco,
                     ori_tipocuenta,
                     ori_cuenta,
                     ori_nit,
                     indtipo,
                     usuario_proceso,
                     fecha_proceso,
                     des_idbanco,
                     des_tipocuenta,
                     des_cuenta,
                     des_nit)
                    select v_fechacorte,
                           v_fechacorte,
                           v_fec_dispersion_con,
                           conces.idconcesionario,
                           17, -- orden
                           (vlrTecho - vlrPagCon),
                           'Valor desde Fondo Renovacion - ' || v_nomConcesionario, -- concepto,
                           v_idbanco,
                           v_tipocta,
                           v_numcta,
                           v_numide,
                           'SIMIT',
                           v_usuario,
                           sysdate,
                           v_idbanco_c,
                           v_tipocuenta_c,
                           v_cuenta_c,
                           v_nit_c
                      from dual;
                
                  update techoutilizado t
                     set t.valordesdefondo =
                         (vlrTecho - vlrPagCon)
                   where t.idconcesionario = conces.idconcesionario
                     and t.tiporecaudo = 'EXT'
                     and t.fechacorte = v_fechacorte;
                
                  commit;
                
                end if; -- fin if2
        */
      end if; -- fin if1
    
    end loop; -- fin ciclo1
  
    v_cod_error := 0000;
    v_msg_error := 'Aplicacion de techo correcta';
  
  EXCEPTION
    WHEN OTHERS THEN
      v_cod_error := SQLCODE;
      v_msg_error := SQLERRM;
    
  END obtieneFondosTecho;

  PROCEDURE validaParametrosTecho(v_fechaCorte date,
                                  v_usuario    varchar2,
                                  v_cod_error  out number,
                                  v_msg_error  out varchar2) as
    CURSOR tConcesionario IS
      select c.idconcesionario, c.fechaprorroga
        from p_dispersion_concesionario c
       where not c.fechaprorroga is null;
  
    idConc char(2);
  
    vlrTecho number;
  BEGIN
  
    for conces in tConcesionario loop
      idConc := conces.idconcesionario;
    
      select count(*)
        into vlrTecho
        from controltecho t
       where t.idconcesionario = conces.idconcesionario
         and t.periodo = to_char(v_fechaCorte, 'yyyymm')
         and t.tiporecaudo = 'EXT';
    
      if (vlrTecho = 0) then
        v_cod_error := 0001;
        v_msg_error := 'Techo no definido para concesionario ' ||
                       conces.idconcesionario;
        return;
      end if;
    
    end loop; -- fin ciclo1
  
    v_cod_error := 0000;
    v_msg_error := 'Aplicacion de techo correcta';
  
  EXCEPTION
    WHEN OTHERS THEN
      v_cod_error := SQLCODE;
      v_msg_error := SQLERRM;
    
  END validaParametrosTecho;

  PROCEDURE aplicaTecho_local(v_fechaIni  date,
                              v_fechaFin  date,
                              v_usuario   varchar2,
                              v_cod_error out number,
                              v_msg_error out varchar2) as
    idConcesionario char(2);
  
    CURSOR tConcesionario IS
      select c.idconcesionario, c.fechaprorroga
        from p_dispersion_concesionario c
       where not c.fechaprorroga is Null
         And C.FECHAPRORROGA <= v_fechaFin
         and c.idconcesionario <> '03';
  
    CURSOR tLiquidacion IS
      select r.*
        from recaudo_local_liquidacion r
       where r.fec_corte >= v_fechaIni
         and r.fec_corte <= v_fechaFin
         and r.idconcesionario = idConcesionario
       order by r.indtipo, r.idrecaudo_local_liquidacion;
  
    vlrTecho  number;
    vlrPagCon number;
  
    vlrPagLiq number;
  
    vlrPagDet number;
  
  BEGIN
    -- poner a ceros fondo renovacion
    update recaudo_local_liquidacion r
       set r.fondorenovacion = 0,
           r.fondorenov27    = 0,
           r.fondorenov3     = 0,
           r.fondorenov35    = 0,
           r.fondorenov7     = 0
     where r.fec_corte >= v_fechaIni
       and r.fec_corte <= v_fechaFin
       and r.fondorenovacion is null;
  
    -- para cada concesionario con prorroga (ciclo1)
    for conces in tConcesionario loop
    
      idConcesionario := conces.idconcesionario;
    
      -- Limpiar procesos anteriores
      delete techoutilizado
       where idconcesionario = conces.idconcesionario
         and tiporecaudo = 'LOC'
         and fechacorte >= v_fechaIni
         and fechacorte <= v_fechaFin;
    
      -- obtener valor del techo para el periodo del concesionario
      select t.valortecho
        into vlrTecho
        from controltecho t
       where t.idconcesionario = conces.idconcesionario
         and t.periodo = to_char(v_fechaIni, 'yyyymm')
         and t.tiporecaudo = 'LOC';
    
      -- actualizar control de techo
      insert into techoutilizado
        (idconcesionario,
         tiporecaudo,
         fechacorte,
         valorsimit7,
         valorpolca35,
         valorsevial3,
         valorsevial27,
         valorhaciafondo,
         valordesdefondo,
         valortecho)
        select r.idconcesionario,
               'LOC',
               r.fec_corte,
               nvl(sum(r.s_valor_2), 0), -- valorsimit7
               nvl(sum(p_valor_8), 0), -- valorpolca35
               0, -- valorsevial3
               0, -- valorsevial27
               nvl(sum(r.fondorenovacion), 0), --valorhaciafondo
               0, --valordesdefondo
               vlrTecho -- valortecho
          from recaudo_local_liquidacion r
         where r.fec_corte >= v_fechaIni
           and r.fec_corte <= v_fechaFin
           and r.idconcesionario = conces.idconcesionario
           and r.fec_tran >= conces.fechaprorroga
         group by r.idconcesionario, r.fec_corte;
    
      commit;
    
    end loop; -- fin ciclo1
  
    v_cod_error := 0000;
    v_msg_error := 'Aplicacion de techo correcta';
  
  EXCEPTION
    WHEN OTHERS THEN
      v_cod_error := SQLCODE;
      v_msg_error := SQLERRM;
    
  END aplicaTecho_local;

