    end;
  
  EXCEPTION
    WHEN OTHERS THEN
    
      p_codError := SQLCODE;
      p_msgError := SQLERRM;
    
  END q_terceroOt;

  PROCEDURE d_terceroOt(p_usuario         varchar2,
                        p_idmunicipio     varchar2,
                        p_nit_t           varchar2,
                        p_jurisdiccion    varchar2,
                        p_medioImposicion varchar2,
                        p_numTercero      varchar2,
                        p_codError        out number,
                        p_msgError        out varchar2) as
  
    numReg number;
  BEGIN
  
    BEGIN
      delete from tercero_ot
       where idmunicipio = p_idmunicipio
         and nit_t = p_nit_t
         and jurisdiccion = p_jurisdiccion
         and medioimposicion = p_medioimposicion
         and numTercero = to_number(p_numTercero);
    
      numReg := 1;
    
      for regTer in (select *
                       from tercero_ot
                      where idmunicipio = p_idmunicipio
                        and jurisdiccion = p_jurisdiccion
                        and medioimposicion = p_medioimposicion
                      order by numTercero) loop
      
        update tercero_ot t
           set t.numTercero = numReg
         where t.idmunicipio = p_idmunicipio
           and t.jurisdiccion = p_jurisdiccion
           and t.medioimposicion = p_medioimposicion
           and t.numtercero = regTer.numTercero;
      
        numReg := numReg + 1;
      end loop;
    
      p_codError := 0;
      p_msgError := 'Registro eliminado correctamente';
    
    EXCEPTION
      WHEN OTHERS THEN
        p_codError := 1;
        p_msgError := 'NO EXISTE REGISTRO';
    end;
  
  EXCEPTION
    WHEN OTHERS THEN
    
      p_codError := SQLCODE;
      p_msgError := SQLERRM;
    
  END d_terceroOt;

  PROCEDURE u_terceroOt(p_usuario         varchar2,
                        p_idmunicipio     varchar2,
                        p_nit_t           varchar2,
                        p_jurisdiccion    varchar2,
