                        p_idbanco         varchar2,
                        p_tipocuenta      varchar2,
                        p_cuenta          varchar2,
                        p_baseLiquidacion number,
                        p_metodoBase      number,
                        p_valorBase       number,
                        p_numTercero      varchar2,
                        p_codError        out number,
                        p_msgError        out varchar2) as
  
    numReg   number;
    w_nombre varchar2(100);
  BEGIN
  
    w_nombre := p_nombre;
  
    if (p_medioImposicion = 'F') then
      w_nombre := w_nombre || ' (F)';
    end if;
  
    if (p_numTercero = '0') then
    
      select count(*)
        into numReg
        from tercero_ot
       where idmunicipio = p_idmunicipio
         and jurisdiccion = p_jurisdiccion
         and medioimposicion = p_medioimposicion;
    
      insert into tercero_ot
        (idmunicipio,
         nit_t,
         nombre,
         correo,
         idbanco,
         tipocuenta,
         cuenta,
         jurisdiccion,
         medioimposicion,
         baseliquidacion,
         metodobase,
         valorbase,
         numTercero)
      values
        (p_idmunicipio,
         p_nit_t,
         w_nombre,
         p_correo,
         p_idbanco,
         p_tipocuenta,
         encriptar(p_cuenta),
         p_jurisdiccion,
         p_medioimposicion,
         p_baseliquidacion,
         p_metodobase,
         p_valorbase,
         numReg + 1);
    
    end if;
  
    if (p_numTercero <> '0') then
      update tercero_ot
         set idmunicipio     = p_idmunicipio,
             nit_t           = p_nit_t,
             nombre          = w_nombre,
             correo          = p_correo,
             idbanco         = p_idbanco,
