             valorbase       = p_valorbase
       where idmunicipio = p_idmunicipio
         and nit_t = p_nit_t
         and jurisdiccion = p_jurisdiccion
         and medioimposicion = p_medioimposicion
         and numTercero = to_number(p_numTercero);
    
    end if;
  
    p_codError := 0;
    p_msgError := 'Operacion correcta';
  
  EXCEPTION
    WHEN OTHERS THEN
    
      p_codError := SQLCODE;
      p_msgError := SQLERRM;
    
  END u_terceroOt;

end PK_TERCERO_OT;
