prompt Creating package body PK_TRFPOLCA
prompt =================================
prompt
create or replace package body PK_TRFPOLCA is

  PROCEDURE u_cta(V_IDBANCO   varchar2,
                  V_TIPOCTA   varchar2,
                  V_TIPOIDE   varchar2,
                  V_NUMIDE    varchar2,
                  V_NUMCTA    varchar2,
                  V_METODO    varchar2,
                  V_VALOR     varchar2,
                  V_ANO       number,
                  V_MES       number,
                  V_DIA       number,
                  V_USUARIO   varchar2,
                  V_COD_ERROR out number,
                  V_MSG_ERROR out varchar2) is

    numReg  number;
    w_fecha date;

  begin

    -- VALIDACIONES
    sp_v_usuario(V_USUARIO, V_MSG_ERROR, 'pk_techo');

    IF not V_MSG_ERROR is null THEN
      V_COD_ERROR := 01;
      V_MSG_ERROR := V_MSG_ERROR;
      return;
    end if;

    select TO_DATE(V_ANO || '-' || V_MES || '-' || V_DIA, 'YYYY-MM-DD')
      into w_fecha
      from DUAL;

    select COUNT(*) INTO numReg from trfpolca where fechatrf = w_fecha;

    IF numReg = 0 THEN
      insert into trfpolca
        (fechatrf,
         idbanco,
         tipocuenta,
         cuenta,
         metodotrf,
         valor,
         nit,
         tipodoc)
      values
        (w_fecha,
         v_idbanco,
         V_TIPOCTA,
         V_NUMCTA,
         V_METODO,
         to_number(V_VALOR),
         V_NUMIDE,
         V_TIPOIDE);

    else
      update trfpolca
         set idbanco    = v_idbanco,
             tipocuenta = V_TIPOCTA,
             cuenta     = V_NUMCTA,
             metodotrf  = V_METODO,
             valor      = to_number(V_VALOR),
             nit        = V_NUMIDE,
             tipodoc    = V_TIPOIDE
       where fechatrf = w_fecha;

    END IF;

    sp_log(V_USUARIO, 'u_ctaTrfPolca', V_NUMCTA);

    commit;

    V_COD_ERROR := 0;
    V_MSG_ERROR := 'Operacion Correcta';

  EXCEPTION
    WHEN OTHERS THEN
      v_cod_error := SQLCODE;
      v_msg_error := SQLERRM;

  END u_cta;

  PROCEDURE d_cta(V_FECHA     varchar2,
                  V_USUARIO   varchar2,
                  V_COD_ERROR out number,
                  V_MSG_ERROR out varchar2) is

    numReg  number;
    w_fecha date;

  begin

    -- VALIDACIONES
    sp_v_usuario(V_USUARIO, V_MSG_ERROR, 'pk_techo');

    IF not V_MSG_ERROR is null THEN
      V_COD_ERROR := 01;
      V_MSG_ERROR := V_MSG_ERROR;
      return;
    end if;

    select TO_DATE(V_FECHA, 'YYYY-MM-DD')
      into w_fecha
      from DUAL;

    select COUNT(*) INTO numReg from trfpolca where fechatrf = w_fecha;

    IF numReg > 0 THEN
      delete trfpolca where fechatrf = w_fecha;
    END IF;

    sp_log(V_USUARIO, 'd_ctaTrfPolca', V_FECHA);

    commit;

    V_COD_ERROR := 0;
    V_MSG_ERROR := 'Operacion Correcta';

  EXCEPTION
    WHEN OTHERS THEN
      v_cod_error := SQLCODE;
      v_msg_error := SQLERRM;

  END d_cta;

end PK_TRFPOLCA;
/

