          --p_error :='Extracto de cuenta ' || v_cuenta || ' del periodo ' || v_periodo || ' ya cargado. Solicitar reproceso' ;
          --return;
    end if;
    
  
    l_clob_len := dbms_lob.getlength(v_cfile);
  
    v_finlinea := chr(13) || chr(10);
  
    l_amount := dbms_lob.instr(v_cfile, v_finlinea, 1, 1);
    IF l_amount < 1 THEN
      v_finlinea := chr(13);
      l_amount   := dbms_lob.instr(v_cfile, v_finlinea, 1, 1);
      IF l_amount < 1 THEN
        v_finlinea := chr(10);
        l_amount   := dbms_lob.instr(v_cfile, v_finlinea, 1, 1);
        IF l_amount < 1 THEN
          p_error := '||Formato desconocido de archivo';
          return;
        end if;
      end if;
    end if;
  
    --Registro #1
    dbms_lob.read(v_cfile, l_amount, l_pos, vBuffer);
 
    if ( instr(lower(vBuffer),lower(v_encabezado)) = 0 ) then
        p_error := 'Encabezado erroneo. encabezado leido: ' ||
                   lower(vBuffer) || '*' || lower(v_encabezado);
        return;
    end if;
  
 
    l_pos := l_pos + l_amount + length(v_finlinea) - 1;
  
    --Informacion
    WHILE l_pos < l_clob_len - 1 and p_error is null LOOP
      l_amount := dbms_lob.instr(v_cfile, v_finlinea, l_pos + 1, 1);
      IF l_amount < 1 THEN
        p_error := 'Registro ' || i || ' Invalido';
        return;
      else
        l_amount := l_amount - l_pos;
        dbms_lob.read(v_cfile, l_amount, l_pos, vBuffer);
      
        linea := vBuffer;
        
        finLinea := false;
        if ( linea = ';;;;;;;;' ) then
           finLinea := true;
        end if;
        
        campo := tokenize(str => linea, delim => ';');
        
        if ( campo(1) is null and not finLinea ) then
               p_error := 'Error en registro ' || i || '. Formato errado. ' ||
                        '. Datos registro: ' || vBuffer;
            return;
    
        end if;
        
        if (not finLinea) then
        BEGIN
          r.numerocuenta     := v_cuenta;
          r.periodo          := v_periodo;
          r.fecha            := to_date(campo(1), 'dd/mm/yyyy');
          r.codigotransaccion := campo(2); 
          r.descripcionmovimiento := campo(3);
          r.referencia := campo(4);
          
          if (campo(5) is null) then
             r.debito := 0;
          else
             r.debito :=  to_number(campo(5), '9999,999,999,999,999.99');
          end if;
          
          if (campo(6) is null) then
             r.credito := 0;
          else
             r.credito :=  to_number(campo(6), '9999,999,999,999,999.99');
          end if;
               
          if (campo(7) is null) then
             r.impuestogmf := 0;
          else
             r.impuestogmf :=  to_number(campo(7), '9999,999,999,999,999.99');
          end if;
          
          r.oficina := campo(8);
          
          EXCEPTION
          WHEN OTHERS THEN
            p_error := 'Error en registro ' || i || '. Mensaje: ' ||
                       SQLERRM || '. Datos registro: ' || vBuffer;
            return;
          END;
          
          end if;
          
      end if;
      
      l_pos := l_pos + l_amount + length(v_finlinea);
    
      if (not finLinea) then
          i     := i + 1;
         insert into mvtoAgrario_temp values r;
      end if;       
    END LOOP;
    
    insert into mvtoagrario
     (numeroTrx, numerocuenta, periodo, fecha, codigotransaccion, descripcionmovimiento, referencia, debito, credito, impuestogmf, oficina)
    select seq_mvto.nextval, numerocuenta, periodo, fecha, codigotransaccion, descripcionmovimiento, referencia, debito, credito, impuestogmf, oficina from mvtoagrario_temp;

    insert into trxsinidentificar
     (idbanco, cuenta, periodo, numerotrx, fechatrx, descripciontrx, valortrx, oficinaorigen, fechacargue, usuariocargue, numsecuenciareg, estadoiden, indTipo, p_consignado)
    select '40', numerocuenta, periodo, numeroTrx, fecha, descripcionmovimiento, credito, oficina, sysdate, p_usuario,seq_ide.nextval, 'SI' , v_indtipo as indTipo, decode(v_indtipo, 'SIMIT', 10, 55) as p_consignado
    from mvtoagrario
    where numerocuenta = v_cuenta
    and periodo = v_periodo
    and descripcionmovimiento in (select concepto from conceptosidentificar where idbanco = '40');

    update trxsinidentificar t
    set t.idmunicipio = ( select min(idmunicipio) from oficinabanco o where
                          o.idbanco = '40' and
                          instr(o.nombre,t.oficinaorigen) > 0 ) 
    where t.cuenta = v_cuenta
    and t.periodo = v_periodo;

    update trxsinidentificar t
    set t.idmunicipio = ( select min(idmunicipio) from oficinabanco o where
                          o.idbanco = '40' and
                          instr(t.oficinaorigen,o.nombre) > 0 ) 
    where t.cuenta = v_cuenta
    and t.periodo = v_periodo
    and t.idmunicipio is null;


    commit;
  
  
    i := i - 1; 
    if p_error is null then
      p_error := 'El archivo se VALIDO exitosamente. Registros: ' || i;
    end if;
  
 
  EXCEPTION
    WHEN OTHERS THEN
      sp_ex(SQLCODE,
            p_usuario || ' [cargarExtAgrario] ' || SQLERRM,
            p_error);
    
  end cargarExtAgrario;


PROCEDURE cargarExtBancafe(p_uploadid        NUMBER,
                              p_idbanco         char,
                              p_cta_recaudo_fcm varchar2,
                              p_usuario         varchar2,
                              p_error           OUT VARCHAR2) is
  
    v_cuenta        varchar2(16);
    v_indtipo       char(5);
    v_cfile         clob;
    l_clob_len      PLS_INTEGER;
    l_pos           PLS_INTEGER := 1;
    l_amount        BINARY_INTEGER := 32767;
    v_encabezado    VARCHAR2(500) := 'Fecha;Cod. Trn.;Descripcion Movimiento;Referencia;Debito;Credito;Imp. GMF;Oficina;'; 
    vBuffer         VARCHAR2(32767);
    v_finlinea      varchar(2);
    v_nombreArchivo varchar2(255);
    v_periodo       char(6);
    r               mvtoBancafe_temp%rowtype;
    linea           varchar2(1000);
  
    i     number := 1;
    tk    number := 1;
    v_chr NUMBER := 13; -- 1 tipo de Enter
  
    v_registros number;
    w_registros number;
    v_total     number;
    finLinea boolean;
    
    
    campo token_list;
    
 
  BEGIN
  
    delete from mvtoBancafe_temp;
  
    v_cuenta  := Substr(p_cta_recaudo_fcm,
                        Instrb(p_cta_recaudo_fcm, '-', 1, 2) + 1,
                        Length(p_cta_recaudo_fcm));
    v_indtipo := Substr(p_cta_recaudo_fcm,
                        1,
                        Instrb(p_cta_recaudo_fcm, '-', 1, 1) - 1);
  
    select cfile, filename
      into v_cfile, v_nombreArchivo
      from upload
     where uploadid = p_uploadid;
  
    v_periodo := Substr(v_nombreArchivo, 1, 6);
    
    select count(*)
    into  v_registros
    from mvtoBancafe
    where numerocuenta = v_cuenta
    and periodo = v_periodo;
    
    if (v_registros > 0 ) then
          delete from mvtoBancafe
          where numerocuenta = v_cuenta
          and periodo = v_periodo;
          
          select count(*)
          into  v_registros
          from trxsinidentificar
          where cuenta = v_cuenta
          and periodo = v_periodo;
                
          if (v_registros > 0) then
          
            delete from trxsinidentificar
            where cuenta = v_cuenta
            and periodo = v_periodo;
          
          end if;

    
    
          --p_error :='Extracto de cuenta ' || v_cuenta || ' del periodo ' || v_periodo || ' ya cargado. Solicitar reproceso' ;
          --return;
    end if;
    
  
    l_clob_len := dbms_lob.getlength(v_cfile);
  
    v_finlinea := chr(13) || chr(10);
  
    l_amount := dbms_lob.instr(v_cfile, v_finlinea, 1, 1);
    IF l_amount < 1 THEN
      v_finlinea := chr(13);
      l_amount   := dbms_lob.instr(v_cfile, v_finlinea, 1, 1);
      IF l_amount < 1 THEN
        v_finlinea := chr(10);
        l_amount   := dbms_lob.instr(v_cfile, v_finlinea, 1, 1);
        IF l_amount < 1 THEN
          p_error := '||Formato desconocido de archivo';
          return;
        end if;
      end if;
    end if;
  
  
    --Informacion
    WHILE l_pos < l_clob_len - 1 and p_error is null LOOP
      l_amount := dbms_lob.instr(v_cfile, v_finlinea, l_pos + 1, 1);
      IF l_amount < 1 THEN
        p_error := 'Registro ' || i || ' Invalido';
        return;
      else
        l_amount := l_amount - l_pos;
        dbms_lob.read(v_cfile, l_amount, l_pos, vBuffer);
      
        linea := vBuffer;
        
        
        BEGIN
          if (v_cuenta <>  substr(linea,20,9) ) then
              p_error := 'Archivo no corresponde a la cuenta. Cuenta del archivo  ' || substr(linea,20,9) || '- Cuenta ingresada: ' || v_cuenta;
              return;
          end if;
        
          r.numerocuenta     := v_cuenta;
          r.periodo          := v_periodo;
          r.tipodoc          := substr(linea,1,1);
          r.numerodocumento  := substr(linea,8,9);
          r.codigoaplicacion := substr(linea,17,1);
          r.numerocta        := substr(linea,20,9);
          r.fechaproceso     := to_date(substr(linea,29,8), 'yyyymmdd');
          r.fechatrx         := to_date(substr(linea,37,8), 'yyyymmdd');
          r.codigotrx        := substr(linea,45,4);
          r.descripciontrx   := trim(substr(linea,49,40));
          r.oficinasede      := substr(linea,89,5);
          r.oficinareceptora := substr(linea,94,5);
          r.nombreoficina    := trim(substr(linea,99,20));
          r.nrodocumento     := substr(linea,119,11);
          r.tipoespecie      := substr(linea,130,2);
          r.valortrx         := to_number(substr(linea,132,15),'999999999999999')/100;
          r.saldoanterior    := to_number(substr(linea,147,15),'999999999999999')/10;
          r.tiporecaudo      := substr(linea,162,1);
          r.fuentetrx        := '';
          r.codigoiac        := '';
          r.referencia1      := substr(linea,177,24);
          r.referencia2      := substr(linea,201,24);
          r.secuencia        := substr(linea,225,9);
          r.tipotrx          := substr(linea,234,1);
          
          EXCEPTION
          WHEN OTHERS THEN
            p_error := 'Error en registro ' || i || '. Mensaje: ' ||
                       SQLERRM || '. Datos registro: ' || vBuffer;
            return;
          END;
          
          
      end if;
      
      l_pos := l_pos + l_amount + length(v_finlinea);
    
      i     := i + 1;
      insert into mvtoBancafe_temp values r;
    
    END LOOP;

    insert into mvtobancafe
      (numeroTrx, numerocuenta, periodo, tipodoc, numerodocumento, codigoaplicacion, numerocta, fechaproceso, fechatrx, codigotrx, descripciontrx, oficinasede, oficinareceptora, nombreoficina, nrodocumento, tipoespecie, valortrx, saldoanterior, tiporecaudo, fuentetrx, codigoiac, referencia1, referencia2, secuencia, tipotrx)
    select seq_mvto.nextval, numerocuenta, periodo, tipodoc, numerodocumento, codigoaplicacion, numerocta, fechaproceso, fechatrx, codigotrx, descripciontrx, oficinasede, oficinareceptora, nombreoficina, nrodocumento, tipoespecie, valortrx, saldoanterior, tiporecaudo, fuentetrx, codigoiac, referencia1, referencia2, secuencia, tipotrx from mvtobancafe_temp;


    insert into trxsinidentificar
     (idbanco, cuenta, periodo, numerotrx, fechatrx, descripciontrx, valortrx, oficinaorigen, fechacargue, usuariocargue, numsecuenciareg, estadoiden, indTipo, p_consignado, observaciones)
    select '50' , numerocuenta, periodo, numeroTrx, fechatrx, descripciontrx, valortrx, nombreoficina, sysdate, p_usuario, seq_ide.nextval, 'SI', v_indtipo as indTipo, decode(v_indtipo, 'SIMIT', 10, 55) as p_consignado, referencia1 || referencia2 
    from mvtobancafe
    where numerocuenta = v_cuenta
    and periodo = v_periodo
    and descripciontrx in (select concepto from conceptosidentificar where idbanco = '50');


    update trxsinidentificar t
    set t.idmunicipio = (select m.idmunicipio
                         from municipio m, mvtobancafe b
                         where t.numerotrx = b.numerotrx and
                         instr(b.descripciontrx,'CONSIGNACION CONVENIO') > 0 and
                         substr(b.referencia2,22,3) = '000' and
                         m.idmunicipio = substr(b.referencia2,17,8))
    where t.cuenta = v_cuenta
    and t.periodo = v_periodo;
    
    update trxsinidentificar t
    set t.idmunicipio = (select min(m.idmunicipio)
                         from municipio m, mvtobancafe b
                         where t.numerotrx = b.numerotrx and
                         instr(b.descripciontrx,'CONSIGNACION CONVENIO') > 0 and
                         substr(b.referencia2,22,3) <> '000' and
                         instr(m.idmunicipio, substr(b.referencia2,20,5)) > 0 )
    where t.cuenta = v_cuenta
    and t.periodo = v_periodo
    and t.idmunicipio is null;
    
    update trxsinidentificar t
    set t.idmunicipio = (select min(m.idmunicipio)
                         from municipio m, mvtobancafe b
                         where t.numerotrx = b.numerotrx and
                         instr(b.descripciontrx,'CONSIGNACION CONVENIO') > 0 and
                         instr(m.nit_m, substr(b.referencia1,16,9)) > 0 )
    where t.cuenta = v_cuenta
    and t.periodo = v_periodo
    and t.idmunicipio is null;
    
    update trxsinidentificar t
    set t.idmunicipio = (select min(m.idmunicipio)
                         from municipio m, mvtobancafe b
                         where t.numerotrx = b.numerotrx and
                         instr(b.descripciontrx,'CONSIGNACION CONVENIO') > 0 and
                         instr(m.nit_m, substr(b.referencia1,15,9)) > 0 )
    where t.cuenta = v_cuenta
    and t.periodo = v_periodo
    and t.idmunicipio is null;

    
    update trxsinidentificar t
    set t.idmunicipio = (select min(m.idmunicipio)
                         from municipio m, mvtobancafe b
                         where t.numerotrx = b.numerotrx and
                         instr(b.descripciontrx,'CONSIGNACION CONVENIO') > 0 and
                         instr(m.nit_cta, substr(b.referencia1,16,9)) > 0 )
    where t.cuenta = v_cuenta
    and t.periodo = v_periodo
    and t.idmunicipio is null;
    
    update trxsinidentificar t
    set t.idmunicipio = (select min(m.idmunicipio)
                         from municipio m, mvtobancafe b
                         where t.numerotrx = b.numerotrx and
                         instr(b.descripciontrx,'CONSIGNACION CONVENIO') > 0 and
                         instr(m.nit_cta, substr(b.referencia1,15,9)) > 0 )
    where t.cuenta = v_cuenta
    and t.periodo = v_periodo
    and t.idmunicipio is null;
    
    
    update trxsinidentificar t
    set t.idmunicipio = ( select min(idmunicipio) from oficinabanco o where
                          o.idbanco = '50' and
                          instr(t.oficinaorigen,o.nombre) > 0 ) 
    where t.cuenta = v_cuenta
    and t.periodo = v_periodo
    and t.idmunicipio is null;
    
    update trxsinidentificar t
    set t.idmunicipio = ( select min(idmunicipio) from oficinabanco o where
                          o.idbanco = '50' and
                          instr(o.nombre, t.oficinaorigen) > 0 ) 
    where t.cuenta = v_cuenta
    and t.periodo = v_periodo
    and t.idmunicipio is null;


    commit;
  
  
    i := i - 1; 
    if p_error is null then
      p_error := 'El archivo se VALIDO exitosamente. Registros: ' || i;
    end if;
  
 
  EXCEPTION
    WHEN OTHERS THEN
      sp_ex(SQLCODE,
            p_usuario || ' [cargarExtBancafe] ' || SQLERRM,
            p_error);
    
  end cargarExtBancafe;
  
  PROCEDURE i_Novmunicipio(p_idmunicipio              varchar2,
                           p_nit_m                    varchar2,
                           p_nombre                   varchar2,
                           p_idconcesionario          varchar2,
                           p_correo                   varchar2,
                           p_indretroactividad        number,
                           p_indliq_s_ext             number,
                           p_indliq_s_local           number,
                           p_indliq_p_ext             number,
                           p_indliq_p_local           number,
                           p_indAcuerdoPago_2002      number,
                           p_idbanco                  varchar2,
                           p_tipocuenta               varchar2,
                           p_cuenta                   varchar2,
                           p_nit_cta                  varchar2,
                           p_municipio_s              number,
                           p_fcm_contravencional_s    number,
                           p_conces_contravencional_s number,
                           p_acuerdopago_s            number,
                           p_municipio_p              number,
                           p_fcm_contravencional_p    number,
                           p_conces_contravencional_p number,
                           p_acuerdopago_p            number,
                           p_usuario                  varchar2,
                           p_fecha_act_ano            number,
                           p_fecha_act_mes            number,
                           p_fecha_act_dia            number,
                           p_firma1                   varchar2,
                           p_firma2                   varchar2,
                           p_dnsAvvillas              varchar2,
                           p_dnsDavivienda            varchar2,
                           p_error                    OUT VARCHAR2,
                           p_novedad                  OUT varchar2
                           
                           ) AS
  
    w_fecha_act       date;
    numNovedad        number;
    m_dias_activacion number;
    min_fecha_act     date;
    correo_usuario    varchar(100);
    correo_fcm        varchar(100);
  
  BEGIN
  
    -- VALIDACIONES
    sp_v_usuario(p_usuario, p_error, 'pk_proc');
  
    select mail
      into correo_usuario
      from person
     where idperson = TO_NUMBER(p_usuario);
  
    select substr(correo, 0, 100)
      into correo_fcm
      from fcm
     where nit_fcm = '8000826650';
  
    select count(*)
      INTO v_valida
      from departamento t
     where iddepartamento = substr(p_idmunicipio, 1, 2);
  
    select TO_DATE(p_fecha_act_ano || '-' || p_fecha_act_mes || '-' ||
                   p_fecha_act_dia,
                   'YYYY-MM-DD')
      into w_fecha_act
      from DUAL;
  
    if (v_valida = 0) then
      p_error := 'Departamento no existe';
      return;
    end if;
  
    select COUNT(*)
      INTO v_valida
      from municipio t
     where idmunicipio = p_idmunicipio;
  
    IF v_valida <> 0 THEN
      p_error := 'municipio ya existente';
      return;
    else
      select dias_activacion into m_dias_activacion from fcm;
      IF m_dias_activacion is null THEN
        p_error := 'Error en parametrización (días activación)';
        return;
      ELSE
        min_fecha_act := fn_fec_dispersion(to_date(to_char(sysdate,
                                                           'yyyy-mm-dd'),
                                                   'yyyy-mm-dd'),
                                           m_dias_activacion,
                                           0,
                                           '99999999');
        IF min_fecha_act > w_fecha_act then
          p_error := 'Fecha Activación Inválida';
          return;
        ELSE
          select seq_novedad.Nextval into numNovedad from dual;
        
          insert into novmunicipio
            (idmunicipio,
             nit_m,
             nombre,
             idconcesionario,
             correo,
             indretroactividad,
             idbanco,
             tipocuenta,
             cuenta,
             nit_cta,
             indliq_s_ext,
             indliq_s_local,
             indliq_p_ext,
             indliq_p_local,
             indAcuerdoPago_2002,
             iddepartamento,
             TIPONOV,
             NUMERONOV,
             FECHANOV,
             USUARIONOV,
             FECHAACT,
             ESTADONOV,
             FIRMA1,
             FIRMA2,
             municipio_s,
             fcm_contravencional_s,
             conces_contravencional_s,
             acuerdopago_s,
             municipio_p,
             fcm_contravencional_p,
             conces_contravencional_p,
             acuerdopago_p,
             dnsavvillas,
             dnsgranbanco)
          values
            (p_idmunicipio,
             p_nit_m,
             p_nombre,
             p_idconcesionario,
             p_correo,
             p_indretroactividad,
             p_idbanco,
             p_tipocuenta,
             encriptar(p_cuenta),
             p_nit_cta,
             p_indliq_s_ext,
             p_indliq_s_local,
             p_indliq_p_ext,
             p_indliq_p_local,
             p_indAcuerdoPago_2002,
             substr(p_idmunicipio, 1, 2),
             '01', -- NOVEDAD ADICION MUNICIPIO
             numNovedad,
             sysdate,
             p_usuario,
             w_fecha_act,
             'INGRESADA',
             p_firma1,
             p_firma2,
             p_municipio_s,
             p_fcm_contravencional_s,
             p_conces_contravencional_s,
             p_acuerdopago_s,
             p_municipio_p,
             p_fcm_contravencional_p,
             p_conces_contravencional_p,
             p_acuerdopago_p,
             p_dnsAvvillas,
             p_dnsDavivienda);
        
          p_error   := MSG_INS_OK;
          p_novedad := '01-' || to_char(numNovedad, '0000000');
        
          sp_envionotificacion(gruporecibe => 4,
                               grupocopia  => -1,
                               asunto      => 'Notificación de Novedad de Municipio:  ' ||
                                              p_novedad,
                               mensaje     => 'Se ha creado una novedad financiera de municipio. Por favor verificar y dar su visto bueno. NUMERO NOVEDAD ' ||
                                              p_novedad ||
                                              ' . Muchas gracias',
                               p_usuario   => p_usuario);
          commit;
        END IF;
      END IF;
    END IF;
  
    sp_log(p_usuario,
           'i_Novmunicipio',
           p_idmunicipio || '-' || p_nit_m || '-' || p_nombre || '-' ||
           p_idconcesionario || '-' || p_correo || '-' ||
           p_indretroactividad || '-' || p_idbanco || '-' || p_tipocuenta || '-' ||
           p_cuenta || '-' || p_nit_cta || '-' || p_indliq_s_ext || '-' ||
