                                                                                                                                                                  c.consecutivo_cartera
    
                                                                                                                                                           )
        where l.fec_corte >= w_fec_cor_ini
          and l.fec_corte <= w_fec_cor_fin;
    */
  
    w_fecha := w_fec_cor_ini;
    while (w_fecha <= w_fec_cor_fin) loop
      sp_paralelo_temp(p_fecha => w_fecha);
      w_fecha := w_fecha + 1;
