       and des_idbanco is null;
  
    commit;
  
    sp_log(p_usuario,
           'Dispersa Recaudo Externo',
           p_fec_ini_ano || '-' || p_fec_ini_mes || '-' || p_fec_ini_dia);
  
    commit;
  
  EXCEPTION
