            p_usuario || ' [dispersa_recaudo_externo] ' || SQLERRM,
            p_error);
  END dispersa_recaudo_externo;

  PROCEDURE causa_gas_recaudo_externo(p_fec_ini_ano number,
                                      p_fec_ini_mes number,
                                      p_fec_ini_dia number,
                                      p_fec_fin_ano number,
                                      p_fec_fin_mes number,
                                      p_fec_fin_dia number,
                                      p_usuario     varchar2,
                                      p_error       OUT VARCHAR2) as
  
    w_fec_cor_ini date;
    w_fec_cor_fin date;
    /*w_fecmin_cor_ini date;*/
    /*w_fecmax_cor_fin date;*/
    w_registros number;
  begin
    -- VALIDACIONES
    sp_v_usuario(p_usuario, p_error, 'pk_proc');
  
    IF not P_ERROR is null THEN
      return;
    END IF;
  
    select TO_DATE(p_fec_ini_ano || '-' || p_fec_ini_mes || '-' ||
                   p_fec_ini_dia,
                   'YYYY-MM-DD')
      into w_fec_cor_ini
      from DUAL;
  
    select TO_DATE(p_fec_fin_ano || '-' || p_fec_fin_mes || '-' ||
                   p_fec_fin_dia,
                   'YYYY-MM-DD')
      into w_fec_cor_fin
      from DUAL;
  
    delete from recaudo_externo_causacion
     where fec_corte_ini = w_fec_cor_ini
       and fec_corte_fin = w_fec_cor_fin
       and operacion = 'GASTO';
  
    COMMIT;
  
    -- obtener partidas vlr neto concesionario
    insert into recaudo_externo_causacion
      (operacion,
       fec_corte_ini,
       fec_corte_fin,
       ori_idbanco,
       ori_cuenta,
       idconcesionario,
       valor_debito,
       valor_credito,
       deb_cta_con,
       deb_cen_cos,
       deb_nit_ter,
       cre_cta_con,
       cre_cen_cos,
       cre_nit_ter,
       orden,
       concepto_con,
       usuario_proceso,
       fecha_proceso,
       indtipo)
      select 'GASTO',
             w_fec_cor_ini,
             w_fec_cor_fin,
             r.idbanco,
             r.cta_recaudo,
             r.id_concesionario_liq,
             null,
             sum(s_valor_28),
             '', -- cuenta debito
             '', -- centro debito
             '', -- nit debito
             max(f.ctacon_puente),
             max(p.concesionario_s_cc),
             max(c.nit_c),
             1, -- orden
             'Por Pagar Recaudo ' || max(indtipo) || ' Externo ' ||
             max(c.nombre) || ' (6.5%) ',
             p_usuario,
             sysdate,
             max(indtipo)
        from recaudo_externo_liquidacion r,
             concesionario               c,
             fcm                         f,
             p_dispersion_concesionario  p
       where r.fec_corte >= w_fec_cor_ini
         and r.fec_corte <= w_fec_cor_fin
         and r.ind_proceso = 1
         and r.indtipo = 'SIMIT'
         and r.id_concesionario_liq = c.idconcesionario
         and r.id_concesionario_liq = p.idconcesionario
       group by r.id_concesionario_liq, r.idbanco, r.cta_recaudo;
  
    -- obtener partidas vlr neto concesionario
    insert into recaudo_externo_causacion
      (operacion,
       fec_corte_ini,
       fec_corte_fin,
       ori_idbanco,
       ori_cuenta,
       idconcesionario,
       valor_debito,
       valor_credito,
       deb_cta_con,
       deb_cen_cos,
       deb_nit_ter,
       cre_cta_con,
       cre_cen_cos,
       cre_nit_ter,
       orden,
       concepto_con,
       usuario_proceso,
       fecha_proceso,
       indtipo)
      select 'GASTO',
             w_fec_cor_ini,
             w_fec_cor_fin,
             r.idbanco,
             r.cta_recaudo,
             r.id_concesionario_liq,
             null, -- valor debito
             sum(p_valor_41), -- valor credito
             '', -- cuenta debito
             '', -- centro debito
             '', -- nit debito
             max(f.ctacon_puente),
             max(p.concesionario_pm_cc),
             max(c.nit_c),
             1, -- orden
             'Por Pagar Recaudo ' || max(indtipo) || ' Externo ' ||
             max(c.nombre) || ' (3.25%) ',
             p_usuario,
             sysdate,
             max(indtipo)
        from recaudo_externo_liquidacion r,
             concesionario               c,
             fcm                         f,
             p_dispersion_concesionario  p
       where r.fec_corte >= w_fec_cor_ini
         and r.fec_corte <= w_fec_cor_fin
         and r.ind_proceso = 1
         and r.indtipo = 'POLCA'
         and r.id_concesionario_liq = c.idconcesionario
         and r.id_concesionario_liq = p.idconcesionario
       group by r.id_concesionario_liq, r.idbanco, r.cta_recaudo;
  
    -- obtener partidas fondo cobertura concesionario
    insert into recaudo_externo_causacion
      (operacion,
       fec_corte_ini,
       fec_corte_fin,
       ori_idbanco,
       ori_cuenta,
       idconcesionario,
       valor_debito,
       valor_credito,
       deb_cta_con,
       deb_cen_cos,
       deb_nit_ter,
       cre_cta_con,
       cre_cen_cos,
       cre_nit_ter,
       orden,
