             w_fec_cor_fin,
             r.idbanco,
             r.cta_recaudo,
             r.id_concesionario_liq,
             null,
             sum(s_valor_5),
             '',
             '',
             '',
             max(f.ctacon_puente),
             max(p.fondo_cob_concesionario_s_cc),
             max(c.nit_c),
             2, -- orden
             'Por Pagar Fondo Cobertura Conces ' || max(indtipo) ||
             ' Externo ' || max(c.nombre) || ' (0.5%) ',
             p_usuario,
             sysdate,
             max(indtipo)
        from recaudo_externo_liquidacion r,
             concesionario               c,
             fcm                         f,
