                        p_acuerdopago_p            number,
                        p_usuario                  varchar2,
                        p_error                    OUT VARCHAR2
                        
                        ) AS
  
  BEGIN
  
    -- VALIDACIONES
    sp_v_usuario(p_usuario, p_error, 'pk_param');
    IF not P_ERROR is null THEN
      return;
    end if;
  
    select count(*)
      INTO v_valida
      from departamento t
     where iddepartamento = substr(p_idmunicipio, 1, 2);
  
    if (v_valida = 0) then
      p_error := 'Departamento no existe';
      return;
    end if;
  
    select COUNT(*)
      INTO v_valida
      from municipio t
     where idmunicipio = p_idmunicipio;
  
    IF v_valida <> 0 THEN
      p_error := 'municipio ya existente';
      return;
    else
      insert into municipio
        (idmunicipio,
         nit_m,
         nombre,
         idconcesionario,
         correo,
         indretroactividad,
         idbanco,
         tipocuenta,
         cuenta,
         nit_cta,
         indliq_s_ext,
         indliq_s_local,
         indliq_p_ext,
         indliq_p_local,
         indAcuerdoPago_2002,
         iddepartamento)
      values
        (p_idmunicipio,
         p_nit_m,
         p_nombre,
         p_idconcesionario,
         p_correo,
         p_indretroactividad,
         p_idbanco,
         p_tipocuenta,
         encriptar(p_cuenta),
         p_nit_cta,
         p_indliq_s_ext,
         p_indliq_s_local,
         p_indliq_p_ext,
         p_indliq_p_local,
