       fechareal,
       hora,
       terminal,
       talon,
       valorcheque,
       valortotal,
       valorunidad,
       oficinarecaudo,
       motivo,
       referencia1,
       referencia2,
       saldoantes,
       indicadorexcento,
       valorexcento,
       canal,
       causaldevolucion,
       jornada,
