      into correo_fcm
      from fcm
     where nit_fcm = '8000826650';
  
    select count(*)
      INTO v_valida
      from departamento t
     where iddepartamento = substr(p_idmunicipio, 1, 2);
  
    select TO_DATE(p_fecha_act_ano || '-' || p_fecha_act_mes || '-' ||
                   p_fecha_act_dia,
