             p_nit_m,
             p_nombre,
             p_idconcesionario,
             p_correo,
             p_indretroactividad,
             p_idbanco,
             p_tipocuenta,
             encriptar(p_cuenta),
             p_nit_cta,
             p_indliq_s_ext,
             p_indliq_s_local,
             p_indliq_p_ext,
             p_indliq_p_local,
             p_indAcuerdoPago_2002,
             substr(p_idmunicipio, 1, 2),
             '01', -- NOVEDAD ADICION MUNICIPIO
             numNovedad,
             sysdate,
             p_usuario,
             w_fecha_act,
             'INGRESADA',
             p_firma1,
