                                              p_novedad ||
                                              ' . Muchas gracias',
                               p_usuario   => p_usuario);
          commit;
        END IF;
      END IF;
    END IF;
  
    sp_log(p_usuario,
           'i_Novmunicipio',
           p_idmunicipio || '-' || p_nit_m || '-' || p_nombre || '-' ||
           p_idconcesionario || '-' || p_correo || '-' ||
           p_indretroactividad || '-' || p_idbanco || '-' || p_tipocuenta || '-' ||
           p_cuenta || '-' || p_nit_cta || '-' || p_indliq_s_ext || '-' ||
