  procedure aplicaNovedadesMunicipio(p_fecha date) is
    w_registros number;
  BEGIN
  
    -- aplica novedades de adicion
  
    select count(*)
      into w_registros
      from novmunicipio n
     where n.estadonov = 'VALIDADA'
       and n.fechaact <= p_fecha
       and n.fechaaplicacion is null
       and n.tiponov = '01';
  
    if (w_registros > 0) then
    
      delete from municipio
       where idmunicipio in (select idmunicipio
                               from novmunicipio
                              where estadonov = 'VALIDADA'
                                and fechaact <= p_fecha
                                and fechaaplicacion is null
                                and tiponov = '01');
    
      insert into municipio
        (idmunicipio,
         nit_m,
         nombre,
         iddepartamento,
         idconcesionario,
         correo,
         indretroactividad,
         idbanco,
         tipocuenta,
         cuenta,
         nit_cta,
         indliq_s_ext,
         indliq_s_local,
         indliq_p_ext,
         indliq_p_local,
         indacuerdopago_2002)
        select idmunicipio,
               nit_m,
               nombre,
               iddepartamento,
               idconcesionario,
               correo,
               indretroactividad,
               idbanco,
               tipocuenta,
               cuenta,
               nit_cta,
               indliq_s_ext,
               indliq_s_local,
               indliq_p_ext,
               indliq_p_local,
               indacuerdopago_2002
          from novmunicipio
         where estadonov = 'VALIDADA'
           and fechaact <= p_fecha
           and fechaaplicacion is null
           and tiponov = '01';
    
      insert into p_dispersion_municipio
        (idmunicipio,
         municipio_s,
         fcm_contravencional_s,
         conces_contravencional_s,
         acuerdopago_s,
         municipio_p,
         fcm_contravencional_p,
         conces_contravencional_p,
         acuerdopago_p)
        select idmunicipio,
               municipio_s,
               fcm_contravencional_s,
               conces_contravencional_s,
               acuerdopago_s,
               municipio_p,
               fcm_contravencional_p,
               conces_contravencional_p,
               acuerdopago_p
          from novmunicipio
         where estadonov = 'VALIDADA'
           and fechaact <= p_fecha
           and fechaaplicacion is null
           and tiponov = '01';
    
      update novmunicipio
         set (fechaaplicacion, estadonov) = (select sysdate, 'ACTIVADA'
                                               from dual)
       where estadonov = 'VALIDADA'
         and fechaact <= p_fecha
         and fechaaplicacion is null
         and tiponov = '01';
    
      commit;
    
    end if;
  
    -- aplica novedades de actualizacion
  
    select count(*)
      into w_registros
      from novmunicipio n
     where n.estadonov = 'VALIDADA'
       and n.fechaact <= p_fecha
       and n.fechaaplicacion is null
       and n.tiponov = '02';
  
    if (w_registros > 0) then
    
      DECLARE
        CURSOR cnov IS
          Select n.idmunicipio,
                 n.nit_m,
                 n.nombre,
                 n.iddepartamento,
                 n.idconcesionario,
                 n.correo,
                 n.indretroactividad,
                 n.idbanco,
                 n.tipocuenta,
                 n.cuenta,
                 n.nit_cta,
                 n.indliq_s_ext,
                 n.indliq_s_local,
                 n.indliq_p_ext,
                 n.indliq_p_local,
                 n.indacuerdopago_2002
            from novmunicipio n
           where n.estadonov = 'VALIDADA'
             and n.fechaaplicacion is null
             and fechaact <= p_fecha
             and n.tiponov = '02'
           order by n.idmunicipio asc, n.fechanov asc;
        cnov_rec cnov%ROWTYPE;
      BEGIN
        FOR cnov_rec IN cnov LOOP
          update municipio
             set idmunicipio         = cnov_rec.idmunicipio,
                 nit_m               = cnov_rec.nit_m,
                 nombre              = cnov_rec.nombre,
                 iddepartamento      = cnov_rec.iddepartamento,
                 idconcesionario     = cnov_rec.idconcesionario,
                 correo              = cnov_rec.correo,
                 indretroactividad   = cnov_rec.indretroactividad,
                 idbanco             = cnov_rec.idbanco,
                 tipocuenta          = cnov_rec.tipocuenta,
                 cuenta              = cnov_rec.cuenta,
                 nit_cta             = cnov_rec.nit_cta,
